        console.log('‚úÖ User is authenticated via session:', req.user.claims?.sub);

        // For quick admin, return the user data directly from session
        if (req.user.claims?.sub === 'quick-admin-user') {
          return res.json({
            id: req.user.claims.sub,
            email: req.user.claims.email,
            firstName: req.user.claims.first_name,
            lastName: req.user.claims.last_name
          });
        }

        // For other users, try to get from database
        const userId = req.user.claims.sub;
        const user = await storage.getUser(userId);
        return res.json(user);
      }

      console.log('‚ùå User not authenticated - session check failed');
      res.status(401).json({ message: "Unauthorized" });
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
