import OpenAI from "openai";

export class AIAssistantService {
  private openai: OpenAI;

  constructor() {
    if (!process.env.OPENAI_API_KEY) {
      throw new Error("OpenAI API key not found");
    }
    this.openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
  }

  async analyzeClaimData(claimData: any): Promise<any> {
    try {
      const prompt = `Analyze this insurance claim data and provide insights:

      Claim Details:
      - Description: ${claimData.description}
      - Incident Date: ${claimData.incidentDate}
      - Estimated Amount: ${claimData.estimatedAmount || 'Not specified'}
      - Location: ${claimData.incidentLocation || 'Not specified'}

      Please provide:
      1. Risk assessment (low/medium/high)
      2. Potential fraud indicators
      3. Recommended next steps
      4. Similar claim patterns to watch for

      Respond in JSON format with these fields: riskLevel, fraudIndicators, recommendations, similarPatterns`;

      const response = await this.openai.chat.completions.create({
        model: "gpt-4o", // the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
        messages: [
          { role: "system", content: "You are an expert insurance claim analyst. Provide detailed, professional analysis in JSON format." },
          { role: "user", content: prompt }
        ],
        response_format: { type: "json_object" }
      });

      return JSON.parse(response.choices[0].message.content || '{}');
    } catch (error) {
      console.error('AI claim analysis error:', error);
      throw new Error('Failed to analyze claim data');
    }
  }

  async answerCustomerQuestion(question: string, context?: any): Promise<string> {
    try {
      const contextInfo = context ? `

      Customer Context:
      - Policy Number: ${context.policyNumber || 'N/A'}
      - Coverage Type: ${context.coverageType || 'N/A'}
      - Policy Status: ${context.policyStatus || 'N/A'}
      ` : '';

      const prompt = `You are a helpful insurance customer service assistant. Answer this customer question professionally and accurately:

      Question: ${question}${contextInfo}

      Provide a clear, helpful response that addresses their concern. If you need additional information to provide a complete answer, ask for it politely.`;

      const response = await this.openai.chat.completions.create({
        model: "gpt-4o", // the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
        messages: [
          { role: "system", content: "You are a knowledgeable and helpful insurance customer service representative." },
          { role: "user", content: prompt }
        ],
        max_tokens: 500
      });

      return response.choices[0].message.content || 'I apologize, but I was unable to generate a response. Please try again or contact support.';
    } catch (error) {
      console.error('AI customer service error:', error);
      throw new Error('Failed to process customer question');
    }
  }

  async generatePolicyRecommendations(customerProfile: any): Promise<any> {
    try {
      const prompt = `Based on this customer profile, recommend appropriate insurance products and coverage levels:

      Customer Profile:
      - Age: ${customerProfile.age || 'Not specified'}
      - Location: ${customerProfile.location || 'Not specified'}
      - Vehicle Type: ${customerProfile.vehicleType || 'Not specified'}
      - Driving History: ${customerProfile.drivingHistory || 'Not specified'}
      - Current Coverage: ${customerProfile.currentCoverage || 'None'}

      Provide recommendations in JSON format with:
      1. recommendedProducts: array of product recommendations
      2. coverageLevels: suggested coverage amounts
      3. discounts: applicable discounts
      4. explanation: reasoning for recommendations`;

      const response = await this.openai.chat.completions.create({
        model: "gpt-4o", // the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
        messages: [
          { role: "system", content: "You are an expert insurance advisor. Provide personalized product recommendations in JSON format." },
          { role: "user", content: prompt }
        ],
        response_format: { type: "json_object" }
      });

      return JSON.parse(response.choices[0].message.content || '{}');
    } catch (error) {
      console.error('AI recommendation error:', error);
      throw new Error('Failed to generate policy recommendations');
    }
  }

  async summarizeClaimHistory(claims: any[]): Promise<string> {
    try {
      const claimsData = claims.map(claim => ({
        date: claim.incidentDate,
        type: claim.type,
        amount: claim.estimatedAmount,
        status: claim.status
      }));

      const prompt = `Summarize this customer's claim history and provide insights:

      Claims: ${JSON.stringify(claimsData, null, 2)}

      Provide a professional summary including:
      - Overall claim frequency
      - Common claim types
      - Risk patterns
      - Recommendations for the customer`;

      const response = await this.openai.chat.completions.create({
        model: "gpt-4o", // the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
        messages: [
          { role: "system", content: "You are an insurance analyst providing customer claim history summaries." },
          { role: "user", content: prompt }
        ],
        max_tokens: 400
      });

      return response.choices[0].message.content || 'Unable to generate claim history summary.';
    } catch (error) {
      console.error('AI claim summary error:', error);
      throw new Error('Failed to summarize claim history');
    }
  }

  async detectAnomalies(data: any, dataType: 'claim' | 'policy' | 'payment'): Promise<any> {
    try {
      const prompt = `Analyze this ${dataType} data for anomalies or unusual patterns:

      Data: ${JSON.stringify(data, null, 2)}

      Look for:
      - Unusual amounts or values
      - Timing irregularities
      - Pattern deviations
      - Potential fraud indicators

      Respond in JSON format with: anomalies (array), riskScore (0-1), explanation`;

      const response = await this.openai.chat.completions.create({
        model: "gpt-4o", // the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
        messages: [
          { role: "system", content: "You are a data analyst specializing in insurance fraud detection and anomaly detection." },
          { role: "user", content: prompt }
        ],
        response_format: { type: "json_object" }
      });

      return JSON.parse(response.choices[0].message.content || '{}');
    } catch (error) {
      console.error('AI anomaly detection error:', error);
      throw new Error('Failed to detect anomalies');
    }
  }
}

server/services/analyticsService.ts.content:
import { storage } from "../storage";
import { type InsertAnalyticsEvent } from "@shared/schema";

export class AnalyticsService {
  async trackEvent(eventData: InsertAnalyticsEvent): Promise<void> {
    try {
      await storage.createAnalyticsEvent(eventData);
    } catch (error) {
      console.error('Analytics tracking error:', error);
      // Don't throw error for analytics to avoid breaking main flows
    }
  }

  async getDashboardMetrics(tenantId: string, dateRange?: { start: Date; end: Date }): Promise<any> {
    try {
      // Get basic metrics with placeholders for demonstration
      const endDate = dateRange?.end || new Date();
      const startDate = dateRange?.start || new Date(Date.now() - 30 * 24 * 60 * 60 * 1000); // 30 days ago

      // In a real implementation, these would be actual database queries
      const metrics = {
        totalPolicies: await this.getPolicyCount(tenantId),
        activeClaims: await this.getActiveClaimsCount(tenantId),
        monthlyPremium: await this.getMonthlyPremiumTotal(tenantId),
        conversionRate: await this.getConversionRate(tenantId),
        recentActivity: await this.getRecentActivity(tenantId),
        chartData: await this.getChartData(tenantId, startDate, endDate)
      };

      return metrics;
    } catch (error) {
      console.error('Dashboard metrics error:', error);
      throw new Error('Failed to fetch dashboard metrics');
    }
  }

  async getRecentActivity(tenantId: string, limit: number = 10): Promise<any[]> {
    try {
      // Mock recent activity data for demonstration
      return [
        {
          id: '1',
          type: 'policy_created',
          message: 'New auto policy POL-2025-ABC123 created',
          timestamp: new Date(Date.now() - 1 * 60 * 60 * 1000), // 1 hour ago
          entityType: 'policy',
          entityId: 'pol-1'
        },
        {
          id: '2',
          type: 'claim_submitted',
          message: 'Claim CLM-2025-DEF456 submitted for review',
          timestamp: new Date(Date.now() - 3 * 60 * 60 * 1000), // 3 hours ago
          entityType: 'claim',
          entityId: 'clm-1'
        },
        {
          id: '3',
          type: 'payment_received',
          message: 'Payment of $1,245.50 received for policy POL-2025-GHI789',
          timestamp: new Date(Date.now() - 6 * 60 * 60 * 1000), // 6 hours ago
          entityType: 'payment',
          entityId: 'pay-1'
        },
        {
          id: '4',
          type: 'quote_generated',
          message: 'Quote QTE-2025-JKL012 generated for RV insurance',
          timestamp: new Date(Date.now() - 12 * 60 * 60 * 1000), // 12 hours ago
          entityType: 'quote',
          entityId: 'qte-1'
        }
      ];
    } catch (error) {
      console.error('Recent activity error:', error);
      throw new Error('Failed to fetch recent activity');
    }
  }

  private async getPolicyCount(tenantId: string): Promise<number> {
    try {
      const policies = await storage.getPolicies(tenantId);
      return policies.length;
    } catch (error) {
      return 0;
    }
  }

  private async getActiveClaimsCount(tenantId: string): Promise<number> {
    try {
      const claims = await storage.getClaims(tenantId, { status: ['submitted', 'under_review', 'approved'] });
      return claims.length;
    } catch (error) {
      return 0;
    }
  }

  private async getMonthlyPremiumTotal(tenantId: string): Promise<number> {
    try {
      const startOfMonth = new Date(new Date().getFullYear(), new Date().getMonth(), 1);
      const policies = await storage.getPolicies(tenantId, { 
        effectiveDate: { gte: startOfMonth } 
      });

      return policies.reduce((total, policy) => {
        return total + (parseFloat(policy.premium) || 0);
      }, 0);
    } catch (error) {
      return 0;
    }
  }

  private async getConversionRate(tenantId: string): Promise<number> {
    try {
      const quotes = await storage.getQuotes(tenantId);
      const policies = await storage.getPolicies(tenantId);

      if (quotes.length === 0) return 0;

      return Math.round((policies.length / quotes.length) * 100 * 100) / 100; // Round to 2 decimals
    } catch (error) {
      return 0;
    }
  }

  private async getChartData(tenantId: string, startDate: Date, endDate: Date): Promise<any> {
    try {
      // Generate mock chart data for demonstration
      const days = [];
      const currentDate = new Date(startDate);

      while (currentDate <= endDate) {
        days.push({
          date: new Date(currentDate).toISOString().split('T')[0],
          policies: Math.floor(Math.random() * 10) + 1,
          claims: Math.floor(Math.random() * 5),
          revenue: Math.floor(Math.random() * 5000) + 1000
        });
        currentDate.setDate(currentDate.getDate() + 1);
      }

      return {
        dailyMetrics: days,
        summary: {
          totalPolicies: days.reduce((sum, day) => sum + day.policies, 0),
          totalClaims: days.reduce((sum, day) => sum + day.claims, 0),
          totalRevenue: days.reduce((sum, day) => sum + day.revenue, 0)
        }
      };
    } catch (error) {
      console.error('Chart data error:', error);
      return { dailyMetrics: [], summary: { totalPolicies: 0, totalClaims: 0, totalRevenue: 0 } };
    }
  }

  async generateReport(tenantId: string, reportType: string, filters?: any): Promise<any> {
    try {
      switch (reportType) {
        case 'policies':
          return await this.generatePolicyReport(tenantId, filters);
        case 'claims':
          return await this.generateClaimsReport(tenantId, filters);
        case 'financial':
          return await this.generateFinancialReport(tenantId, filters);
        default:
          throw new Error('Invalid report type');
      }
    } catch (error) {
      console.error('Report generation error:', error);
      throw new Error('Failed to generate report');
    }
  }

  private async generatePolicyReport(tenantId: string, filters?: any): Promise<any> {
    const policies = await storage.getPolicies(tenantId, filters);

    return {
      reportType: 'policies',
      generatedAt: new Date(),
      totalPolicies: policies.length,
      activePolicies: policies.filter(p => p.status === 'active').length,
      totalPremium: policies.reduce((sum, p) => sum + parseFloat(p.premium), 0),
      policies: policies
    };
  }

  private async generateClaimsReport(tenantId: string, filters?: any): Promise<any> {
    const claims = await storage.getClaims(tenantId, filters);

    return {
      reportType: 'claims',
      generatedAt: new Date(),
      totalClaims: claims.length,
      openClaims: claims.filter(c => ['submitted', 'under_review'].includes(c.status)).length,
      settledClaims: claims.filter(c => c.status === 'settled').length,
      claims: claims
    };
  }

  private async generateFinancialReport(tenantId: string, filters?: any): Promise<any> {
    const policies = await storage.getPolicies(tenantId, filters);
    const payments = await storage.getPayments(tenantId, filters);

    return {
      reportType: 'financial',
      generatedAt: new Date(),
      totalPremium: policies.reduce((sum, p) => sum + parseFloat(p.premium), 0),
      totalPayments: payments.reduce((sum, p) => sum + parseFloat(p.amount), 0),
      policies: policies.length,
      payments: payments.length
    };
  }
}

server/services/claimsService.ts.content:
import { storage } from "../storage";
import { type InsertClaim } from "@shared/schema";

export class ClaimsService {
  async createClaim(claimData: InsertClaim): Promise<any> {
    try {
      // Generate claim number
      const claimNumber = `CLM-${Date.now()}-${Math.random().toString(36).substr(2, 6).toUpperCase()}`;

      // Create claim with initial status
      const claim = await storage.createClaim({
        ...claimData,
        claimNumber,
        status: 'open'
      });

      // Auto-assign adjuster (placeholder logic)
      await this.autoAssignAdjuster(claim.id);

      return claim;
    } catch (error) {
      console.error('Claim creation error:', error);
      throw new Error('Failed to create claim');
    }
  }

  async updateClaim(claimId: string, claimUpdate: any, userId?: string): Promise<any> {
    try {
      // Track who made the update
      const updates = {
        ...claimUpdate,
        updatedAt: new Date()
      };

      if (userId) {
        // Add to audit trail
        const auditEntry = {
          timestamp: new Date(),
          userId,
          action: 'claim_updated',
          changes: Object.keys(claimUpdate)
        };

        const currentClaim = await storage.getClaim(claimId);
        const existingAudit = currentClaim?.auditTrail || [];
        updates.auditTrail = [...existingAudit, auditEntry];
      }

      const claim = await storage.updateClaim(claimId, updates);
      return claim;
    } catch (error) {
      console.error('Claim update error:', error);
      throw new Error('Failed to update claim');
    }
  }

  async processClaim(claimId: string, action: string, notes?: string, adjusterId?: string): Promise<any> {
    try {
      const updates: any = {
        lastUpdated: new Date()
      };

      switch (action) {
        case 'review':
          updates.status = 'under_review';
          break;
        case 'approve':
          updates.status = 'approved';
          updates.approvedAt = new Date();
          break;
        case 'deny':
          updates.status = 'denied';
          updates.deniedAt = new Date();
          break;
        case 'settle':
          updates.status = 'settled';
          updates.settledAt = new Date();
          break;
        case 'close':
          updates.status = 'closed';
          updates.closedAt = new Date();
          break;
      }

      if (adjusterId) {
        updates.adjusterId = adjusterId;
      }

      if (notes) {
        updates.adjustmentNotes = notes;
      }

      const claim = await storage.updateClaim(claimId, updates);

      // Track analytics event
      await storage.createAnalyticsEvent({
        tenantId: claim.tenantId!,
        eventType: 'claim_updated',
        entityType: 'claim',
        entityId: claimId,
        properties: {
          action,
          status: updates.status,
          adjusterId
        }
      });

      return claim;
    } catch (error) {
      console.error('Claim processing error:', error);
      throw new Error('Failed to process claim');
    }
  }

  async estimateDamage(claimId: string, photos?: string[]): Promise<any> {
    try {
      // Placeholder for AI-powered damage estimation
      // In a real implementation, this would use computer vision
      // to analyze damage photos and estimate repair costs

      const estimatedAmount = Math.floor(Math.random() * 10000) + 1000; // Random estimate for demo

      await storage.updateClaim(claimId, {
        estimatedAmount: estimatedAmount.toString(),
        lastUpdated: new Date()
      });

      return {
        claimId,
        estimatedAmount,
        confidence: 0.85,
        method: 'AI Analysis',
        photos: photos || []
      };
    } catch (error) {
      console.error('Damage estimation error:', error);
      throw new Error('Failed to estimate damage');
    }
  }

  async detectFraud(claimId: string): Promise<any> {
    try {
      const claim = await storage.getClaim(claimId);
      if (!claim) {
        throw new Error('Claim not found');
      }

      // Placeholder fraud detection logic
      // In a real implementation, this would use ML models to analyze:
      // - Claim patterns
      // - Customer history
      // - Damage photos
      // - Location data
      // - Time patterns

      const riskFactors = [];
      let riskScore = 0;

      // Example risk factors
      if (claim.incidentDate) {
        const incidentDate = new Date(claim.incidentDate);
        const hourOfDay = incidentDate.getHours();

        // Higher risk for incidents at unusual hours
        if (hourOfDay < 6 || hourOfDay > 22) {
          riskFactors.push('Incident occurred at unusual hour');
          riskScore += 0.2;
        }
      }

      // Check for high claim amount
      if (claim.estimatedAmount && parseFloat(claim.estimatedAmount) > 5000) {
        riskFactors.push('High claim amount');
        riskScore += 0.3;
      }

      // Determine risk level
      let riskLevel = 'low';
      if (riskScore > 0.7) {
        riskLevel = 'high';
      } else if (riskScore > 0.4) {
        riskLevel = 'medium';
      }

      return {
        claimId,
        riskScore,
        riskLevel,
        riskFactors,
