import { Card, CardContent } from "@/components/ui/card";
import { 
  FileTextIcon, 
  ClipboardListIcon, 
  DollarSignIcon, 
  PercentIcon,
  TrendingUpIcon,
  TrendingDownIcon
} from "lucide-react";

interface MetricsCardsProps {
  analytics?: any;
  isLoading?: boolean;
}

export default function MetricsCards({ analytics, isLoading }: MetricsCardsProps) {
  const metrics = [
    {
      title: "Active Policies",
