import { useState, useRef, useEffect } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  Bot, 
  Send, 
  User, 
  MessageCircle, 
  FileText,
  HelpCircle,
  Lightbulb,
  AlertCircle,
  Copy,
  ThumbsUp,
  ThumbsDown,
  RefreshCw
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface ChatMessage {
  id: string;
  type: "user" | "assistant";
  content: string;
  timestamp: Date;
  context?: string;
  helpful?: boolean;
}

interface AssistantContext {
  type: "general" | "claims" | "quotes" | "policy" | "technical";
  data?: any;
}

export default function AIAssistant() {
  const { toast } = useToast();
  const [messages, setMessages] = useState<ChatMessage[]>([
    {
      id: "welcome",
      type: "assistant",
      content: "Hello! I'm your TPA platform AI assistant. I can help you with policy questions, claims guidance, quote explanations, and operational support. How can I assist you today?",
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState("");
  const [context, setContext] = useState<AssistantContext>({ type: "general" });
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Fetch knowledge base topics for context
  const { data: knowledgeTopics } = useQuery({
    queryKey: ["/api/ai/knowledge-topics"],
  });

  // Send message to AI assistant
  const sendMessageMutation = useMutation({
    mutationFn: async (data: { message: string; context: AssistantContext; history: ChatMessage[] }) => {
      return await apiRequest("POST", "/api/ai/chat", data);
    },
    onMutate: () => {
      setIsTyping(true);
    },
    onSuccess: (response) => {
      const assistantMessage: ChatMessage = {
        id: `assistant-${Date.now()}`,
        type: "assistant",
        content: response.message,
        timestamp: new Date(),
        context: response.context
      };
      setMessages(prev => [...prev, assistantMessage]);
      setIsTyping(false);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to get AI response. Please try again.",
        variant: "destructive",
      });
      setIsTyping(false);
    },
  });

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage: ChatMessage = {
      id: `user-${Date.now()}`,
      type: "user",
      content: input.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);

    sendMessageMutation.mutate({
      message: input.trim(),
      context,
      history: messages.slice(-10) // Send last 10 messages for context
    });

    setInput("");
  };

  const handleFeedback = async (messageId: string, helpful: boolean) => {
    try {
      await apiRequest("POST", "/api/ai/feedback", { messageId, helpful });
      setMessages(prev => 
        prev.map(msg => 
          msg.id === messageId ? { ...msg, helpful } : msg
        )
      );
      toast({
        title: "Feedback Recorded",
        description: "Thank you for helping improve our AI assistant",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to record feedback",
        variant: "destructive",
      });
    }
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied",
      description: "Message copied to clipboard",
    });
  };

  const quickActions = [
    { label: "Explain claim process", context: "claims", query: "How do I file a claim?" },
    { label: "Quote pricing help", context: "quotes", query: "Why is my quote this price?" },
    { label: "Policy coverage", context: "policy", query: "What does my policy cover?" },
    { label: "Technical support", context: "technical", query: "I'm having technical issues" }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          {/* Header */}
          <div className="text-center mb-8">
            <div className="flex items-center justify-center mb-4">
              <Bot className="h-12 w-12 text-blue-600 mr-3" />
              <h1 className="text-3xl font-bold text-gray-900">AI Assistant</h1>
            </div>
            <p className="text-gray-600">
              Get instant help with policies, claims, quotes, and platform guidance
            </p>
          </div>

          <div className="grid lg:grid-cols-4 gap-6">
            {/* Context & Quick Actions */}
            <div className="lg:col-span-1 space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="text-sm">Context</CardTitle>
                </CardHeader>
                <CardContent>
                  <Select 
                    value={context.type}
                    onValueChange={(value: any) => setContext({ type: value })}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="general">General Help</SelectItem>
                      <SelectItem value="claims">Claims Support</SelectItem>
                      <SelectItem value="quotes">Quote Questions</SelectItem>
                      <SelectItem value="policy">Policy Info</SelectItem>
                      <SelectItem value="technical">Technical Support</SelectItem>
                    </SelectContent>
                  </Select>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="text-sm">Quick Actions</CardTitle>
                </CardHeader>
                <CardContent className="space-y-2">
                  {quickActions.map((action, index) => (
                    <Button
                      key={index}
                      variant="outline"
                      size="sm"
                      className="w-full justify-start text-xs"
                      onClick={() => {
                        setContext({ type: action.context as any });
                        setInput(action.query);
                      }}
                    >
                      <HelpCircle className="h-3 w-3 mr-2" />
                      {action.label}
                    </Button>
                  ))}
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="text-sm">Features</CardTitle>
                </CardHeader>
                <CardContent className="space-y-3">
                  <div className="flex items-center text-xs text-gray-600">
                    <Lightbulb className="h-3 w-3 mr-2 text-yellow-500" />
                    Smart contextual responses
                  </div>
                  <div className="flex items-center text-xs text-gray-600">
                    <FileText className="h-3 w-3 mr-2 text-blue-500" />
                    Policy document analysis
                  </div>
                  <div className="flex items-center text-xs text-gray-600">
                    <MessageCircle className="h-3 w-3 mr-2 text-green-500" />
                    24/7 availability
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Chat Interface */}
            <div className="lg:col-span-3">
              <Card className="h-[600px] flex flex-col">
                <CardHeader className="border-b">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center">
                      <Bot className="h-5 w-5 text-blue-600 mr-2" />
                      <CardTitle className="text-lg">TPA Assistant</CardTitle>
                      <Badge className="ml-2" variant="secondary">
                        {context.type}
                      </Badge>
                    </div>
                    <Button 
                      variant="ghost" 
                      size="sm"
                      onClick={() => setMessages([messages[0]])}
                    >
                      <RefreshCw className="h-4 w-4" />
                    </Button>
                  </div>
                </CardHeader>

                {/* Messages */}
                <CardContent className="flex-1 overflow-y-auto p-4 space-y-4">
                  {messages.map((message) => (
                    <div
                      key={message.id}
                      className={`flex ${message.type === "user" ? "justify-end" : "justify-start"}`}
                    >
                      <div
                        className={`max-w-[80%] rounded-lg p-3 ${
                          message.type === "user"
                            ? "bg-blue-600 text-white"
                            : "bg-gray-100 text-gray-900"
                        }`}
                      >
                        <div className="flex items-start space-x-2">
                          {message.type === "assistant" && (
                            <Bot className="h-4 w-4 mt-0.5 flex-shrink-0" />
                          )}
                          {message.type === "user" && (
                            <User className="h-4 w-4 mt-0.5 flex-shrink-0" />
                          )}
                          <div className="flex-1">
                            <div className="text-sm whitespace-pre-wrap">
                              {message.content}
                            </div>
                            <div className="flex items-center justify-between mt-2">
                              <div className="text-xs opacity-70">
                                {message.timestamp.toLocaleTimeString()}
                              </div>
                              <div className="flex items-center space-x-1">
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  className="h-6 w-6 p-0"
                                  onClick={() => copyToClipboard(message.content)}
                                >
                                  <Copy className="h-3 w-3" />
                                </Button>
                                {message.type === "assistant" && message.id !== "welcome" && (
                                  <>
                                    <Button
                                      variant="ghost"
                                      size="sm"
                                      className="h-6 w-6 p-0"
                                      onClick={() => handleFeedback(message.id, true)}
                                    >
                                      <ThumbsUp 
                                        className={`h-3 w-3 ${
                                          message.helpful === true ? "text-green-600" : ""
                                        }`} 
                                      />
                                    </Button>
                                    <Button
                                      variant="ghost"
                                      size="sm"
                                      className="h-6 w-6 p-0"
                                      onClick={() => handleFeedback(message.id, false)}
                                    >
                                      <ThumbsDown 
                                        className={`h-3 w-3 ${
                                          message.helpful === false ? "text-red-600" : ""
                                        }`} 
                                      />
                                    </Button>
                                  </>
                                )}
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}

                  {isTyping && (
                    <div className="flex justify-start">
                      <div className="bg-gray-100 text-gray-900 rounded-lg p-3">
                        <div className="flex items-center space-x-2">
                          <Bot className="h-4 w-4" />
                          <div className="flex space-x-1">
                            <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                            <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                            <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}

                  <div ref={messagesEndRef} />
                </CardContent>

                {/* Input */}
                <div className="border-t p-4">
                  <form onSubmit={handleSendMessage} className="flex space-x-2">
                    <Input
                      value={input}
                      onChange={(e) => setInput(e.target.value)}
                      placeholder="Ask a question about policies, claims, or quotes..."
                      disabled={sendMessageMutation.isPending}
                      className="flex-1"
                    />
                    <Button 
                      type="submit" 
                      disabled={!input.trim() || sendMessageMutation.isPending}
                    >
                      <Send className="h-4 w-4" />
                    </Button>
                  </form>
                  <p className="text-xs text-gray-500 mt-2">
                    AI responses may contain errors. Always verify important information with official documentation.
                  </p>
                </div>
              </Card>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/AdminAiModels.tsx.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Slider } from '@/components/ui/slider';
import { Switch } from '@/components/ui/switch';
import { ArrowLeft, Brain, Settings, Zap, CheckCircle, AlertCircle, Bot } from 'lucide-react';
import { Link } from 'wouter';
import { useToast } from '@/hooks/use-toast';

export default function AdminAiModels() {
  const [selectedModel, setSelectedModel] = useState('gpt-4o');
  const [temperature, setTemperature] = useState([0.7]);
  const [maxTokens, setMaxTokens] = useState('2048');
  const [enableFunctionCalling, setEnableFunctionCalling] = useState(true);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: modelConfig, isLoading } = useQuery({
    queryKey: ['/api/admin/ai-models'],
    retry: false,
  });

  const updateModelMutation = useMutation({
    mutationFn: async (config: any) => {
      const response = await fetch('/api/admin/ai-models', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(config),
      });
      if (!response.ok) throw new Error(await response.text());
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Model Updated",
        description: "AI model configuration has been updated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/ai-models'] });
    },
    onError: (error: Error) => {
      toast({
        title: "Update Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const availableModels = [
    {
      id: 'gpt-4o',
      name: 'GPT-4o',
      provider: 'OpenAI',
      status: 'active',
      description: 'Latest multimodal model with vision and text capabilities',
      maxTokens: 128000,
      costPer1K: 0.005
    },
    {
      id: 'gpt-4-turbo',
      name: 'GPT-4 Turbo',
      provider: 'OpenAI',
      status: 'active',
      description: 'High-performance model with 128K context window',
      maxTokens: 128000,
      costPer1K: 0.01
    },
    {
      id: 'gpt-3.5-turbo',
      name: 'GPT-3.5 Turbo',
      provider: 'OpenAI',
      status: 'active',
      description: 'Fast and cost-effective model for most tasks',
      maxTokens: 16385,
      costPer1K: 0.0015
    }
  ];

  const handleSaveConfiguration = () => {
    updateModelMutation.mutate({
      model: selectedModel,
      temperature: temperature[0],
      maxTokens: parseInt(maxTokens),
      enableFunctionCalling,
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'active':
        return <Badge className="bg-green-100 text-green-800">Active</Badge>;
      case 'deprecated':
        return <Badge className="bg-yellow-100 text-yellow-800">Deprecated</Badge>;
      case 'inactive':
        return <Badge className="bg-red-100 text-red-800">Inactive</Badge>;
      default:
        return <Badge variant="secondary">Unknown</Badge>;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center space-x-4">
            <Link href="/admin">
              <Button variant="ghost" size="sm">
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back to Admin
              </Button>
            </Link>
            <h1 className="text-2xl font-bold text-gray-900">AI Models Configuration</h1>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        <div className="space-y-6">
          {/* Current Configuration */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Brain className="h-5 w-5 mr-2" />
                Current Model Configuration
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <Label htmlFor="modelSelect">Active Model</Label>
                  <Select value={selectedModel} onValueChange={setSelectedModel}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {availableModels.map((model) => (
                        <SelectItem key={model.id} value={model.id}>
                          {model.name} ({model.provider})
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="maxTokens">Max Tokens</Label>
                  <Input
                    id="maxTokens"
                    type="number"
                    value={maxTokens}
                    onChange={(e) => setMaxTokens(e.target.value)}
                    min="1"
                    max="128000"
                  />
                </div>

                <div className="space-y-2">
                  <Label>Temperature: {temperature[0]}</Label>
                  <Slider
                    value={temperature}
                    onValueChange={setTemperature}
                    max={2}
                    min={0}
                    step={0.1}
                    className="w-full"
                  />
                  <div className="flex justify-between text-sm text-gray-500">
                    <span>Conservative</span>
                    <span>Creative</span>
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <Switch
                    id="functionCalling"
                    checked={enableFunctionCalling}
                    onCheckedChange={setEnableFunctionCalling}
                  />
                  <Label htmlFor="functionCalling">Enable Function Calling</Label>
                </div>
              </div>

              <Button onClick={handleSaveConfiguration} disabled={updateModelMutation.isPending}>
                {updateModelMutation.isPending ? 'Saving...' : 'Save Configuration'}
              </Button>
            </CardContent>
          </Card>

          {/* Available Models */}
          <Card>
            <CardHeader>
              <CardTitle>Available Models</CardTitle>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="text-center py-8">Loading models...</div>
              ) : (
                <div className="space-y-4">
                  {availableModels.map((model) => (
                    <div key={model.id} className="flex items-center justify-between p-4 border rounded-lg">
                      <div className="flex items-center space-x-4">
                        <Bot className="h-8 w-8 text-gray-600" />
                        <div>
                          <h3 className="font-medium">{model.name}</h3>
                          <p className="text-sm text-gray-600">{model.description}</p>
                          <div className="flex items-center space-x-4 mt-1 text-xs text-gray-500">
                            <span>Provider: {model.provider}</span>
                            <span>Max Tokens: {model.maxTokens.toLocaleString()}</span>
                            <span>Cost: ${model.costPer1K}/1K tokens</span>
                          </div>
                        </div>
                      </div>
                      <div className="flex items-center space-x-3">
                        {selectedModel === model.id && (
                          <CheckCircle className="h-5 w-5 text-green-600" />
                        )}
                        {getStatusBadge(model.status)}
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => setSelectedModel(model.id)}
                          disabled={selectedModel === model.id}
                        >
                          {selectedModel === model.id ? 'Active' : 'Select'}
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Usage Statistics */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Zap className="h-5 w-5 mr-2" />
                Usage Statistics (Last 30 Days)
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="text-center">
                  <div className="text-2xl font-bold text-blue-600">2,847</div>
                  <div className="text-sm text-gray-600">Total Requests</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-600">1.2M</div>
                  <div className="text-sm text-gray-600">Tokens Processed</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-orange-600">$47.85</div>
                  <div className="text-sm text-gray-600">Total Cost</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-purple-600">98.5%</div>
                  <div className="text-sm text-gray-600">Success Rate</div>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

client/src/pages/AdminApiIntegrations.ts.content:
import { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { ArrowLeft, Plug, CheckCircle, AlertCircle, Car, Brain, CreditCard, Database, Settings, TestTube } from 'lucide-react';
import { Link } from 'wouter';

export default function AdminApiIntegrations() {
  const [editDialog, setEditDialog] = useState<{open: boolean, integration: any}>({open: false, integration: null});
  const [testDialog, setTestDialog] = useState<{open: boolean, integration: any, result: any}>({open: false, integration: null, result: null});
  const [editForm, setEditForm] = useState<any>({});
  const { toast } = useToast();

  const { data: integrations, isLoading } = useQuery({
    queryKey: ['/api/admin/integrations'],
    retry: false,
  });

  const testMutation = useMutation({
    mutationFn: async (integrationId: string) => {
      return await apiRequest(`/api/admin/integrations/${integrationId}/test`, {
        method: 'POST'
      });
    },
    onSuccess: (result, integrationId) => {
      const integration = apiIntegrations.find(i => i.id === integrationId);
      setTestDialog({open: true, integration, result});
    },
    onError: (error) => {
      toast({
        title: "Test Failed",
        description: "Unable to test the integration connection.",
        variant: "destructive",
      });
    }
  });

  const updateMutation = useMutation({
    mutationFn: async ({integrationId, data}: {integrationId: string, data: any}) => {
      return await apiRequest(`/api/admin/integrations/${integrationId}`, {
        method: 'PUT',
        body: data
      });
    },
    onSuccess: () => {
      toast({
        title: "Integration Updated",
        description: "The integration settings have been successfully updated.",
      });
      setEditDialog({open: false, integration: null});
    },
    onError: () => {
      toast({
        title: "Update Failed",
        description: "Unable to update the integration settings.",
        variant: "destructive",
      });
    }
  });

  const handleEdit = (integration: any) => {
    setEditForm({
      apiKey: '‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢',
      endpoint: integration.endpoint,
      timeout: '30000',
      retries: '3'
    });
    setEditDialog({open: true, integration});
  };

  const handleTest = (integration: any) => {
    testMutation.mutate(integration.id);
  };

  const handleSave = () => {
    if (editDialog.integration) {
      updateMutation.mutate({
        integrationId: editDialog.integration.id,
        data: editForm
      });
    }
  };

  const apiIntegrations = [
    {
      id: 'vin-decode',
      name: 'VIN Decoding Service',
      description: 'NHTSA API for vehicle identification and specifications',
      status: 'connected',
      icon: Car,
      endpoint: 'https://vpic.nhtsa.dot.gov/api/',
      lastTested: '2025-08-11',
      responseTime: '188ms'
    },
    {
      id: 'helcim-payments',
      name: 'Helcim Payment Gateway',
      description: 'Credit card processing and payment management',
      status: 'configured',
      icon: CreditCard,
      endpoint: 'https://api.helcim.com/v2/',
      lastTested: '2025-08-11',
      responseTime: '245ms'
    },
    {
      id: 'openai',
      name: 'OpenAI API',
      description: 'AI assistant and natural language processing',
      status: 'connected',
      icon: Brain,
      endpoint: 'https://api.openai.com/v1/',
      lastTested: '2025-08-11',
      responseTime: '892ms'
    },
    {
      id: 'postgres',
      name: 'PostgreSQL Database',
      description: 'Primary database for application data',
      status: 'connected',
      icon: Database,
      endpoint: 'Neon Serverless PostgreSQL',
      lastTested: '2025-08-11',
      responseTime: '45ms'
    }
  ];

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'connected':
        return <Badge className="bg-green-100 text-green-800">Connected</Badge>;
      case 'configured':
        return <Badge className="bg-blue-100 text-blue-800">Configured</Badge>;
      case 'error':
        return <Badge className="bg-red-100 text-red-800">Error</Badge>;
      default:
        return <Badge variant="secondary">Unknown</Badge>;
    }
  };

  const getStatusIcon = (status: string) => {
    if (status === 'connected' || status === 'configured') {
      return <CheckCircle className="h-5 w-5 text-green-600" />;
    }
    return <AlertCircle className="h-5 w-5 text-red-600" />;
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center space-x-4">
            <Link href="/admin">
              <Button variant="ghost" size="sm">
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back to Admin
              </Button>
            </Link>
            <h1 className="text-2xl font-bold text-gray-900">API Integrations</h1>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Plug className="h-5 w-5 mr-2" />
                Active Integrations
              </CardTitle>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="text-center py-8">Loading integrations...</div>
              ) : (
                <div className="space-y-4">
                  {apiIntegrations.map((integration) => {
                    const IconComponent = integration.icon;
                    return (
                      <div key={integration.id} className="flex items-center justify-between p-4 border rounded-lg">
                        <div className="flex items-center space-x-4">
                          <IconComponent className="h-8 w-8 text-gray-600" />
                          <div>
                            <h3 className="font-medium">{integration.name}</h3>
                            <p className="text-sm text-gray-600">{integration.description}</p>
                            <div className="flex items-center space-x-4 mt-1 text-xs text-gray-500">
                              <span>Endpoint: {integration.endpoint}</span>
                              <span>Response: {integration.responseTime}</span>
                              <span>Last tested: {integration.lastTested}</span>
                            </div>
                          </div>
                        </div>
                        <div className="flex items-center space-x-3">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleTest(integration)}
                            disabled={testMutation.isPending}
                          >
                            <TestTube className="h-4 w-4 mr-1" />
                            Test
                          </Button>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleEdit(integration)}
                          >
                            <Settings className="h-4 w-4 mr-1" />
                            Edit
                          </Button>
                          {getStatusIcon(integration.status)}
                          {getStatusBadge(integration.status)}
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Integration Health Summary</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-600">4</div>
                  <div className="text-sm text-gray-600">Active Integrations</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-blue-600">100%</div>
                  <div className="text-sm text-gray-600">Uptime</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-orange-600">342ms</div>
                  <div className="text-sm text-gray-600">Avg Response</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-gray-600">0</div>
                  <div className="text-sm text-gray-600">Failed Requests</div>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>

      {/* Edit Integration Dialog */}
      <Dialog open={editDialog.open} onOpenChange={() => setEditDialog({open: false, integration: null})}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Edit {editDialog.integration?.name}</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="apiKey">API Key</Label>
              <Input
                id="apiKey"
                type="password"
                value={editForm.apiKey || ''}
                onChange={(e) => setEditForm({...editForm, apiKey: e.target.value})}
                placeholder="Enter API key"
              />
            </div>
            <div>
              <Label htmlFor="endpoint">Endpoint URL</Label>
              <Input
                id="endpoint"
                value={editForm.endpoint || ''}
                onChange={(e) => setEditForm({...editForm, endpoint: e.target.value})}
                placeholder="https://api.example.com/v1"
              />
            </div>
            <div>
              <Label htmlFor="timeout">Timeout (ms)</Label>
              <Input
                id="timeout"
                type="number"
                value={editForm.timeout || ''}
                onChange={(e) => setEditForm({...editForm, timeout: e.target.value})}
                placeholder="30000"
              />
            </div>
            <div>
              <Label htmlFor="retries">Max Retries</Label>
              <Input
                id="retries"
                type="number"
                value={editForm.retries || ''}
                onChange={(e) => setEditForm({...editForm, retries: e.target.value})}
                placeholder="3"
              />
            </div>
            <div className="flex justify-end space-x-2">
              <Button 
                variant="outline" 
                onClick={() => setEditDialog({open: false, integration: null})}
              >
                Cancel
              </Button>
              <Button 
                onClick={handleSave}
                disabled={updateMutation.isPending}
              >
                Save Changes
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Test Results Dialog */}
      <Dialog open={testDialog.open} onOpenChange={() => setTestDialog({open: false, integration: null, result: null})}>
        <DialogContent className="max-w-lg">
          <DialogHeader>
            <DialogTitle>Test Results - {testDialog.integration?.name}</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            {testDialog.result ? (
              <>
                <div className="flex items-center space-x-2">
                  {testDialog.result.success ? (
                    <CheckCircle className="h-5 w-5 text-green-600" />
                  ) : (
                    <AlertCircle className="h-5 w-5 text-red-600" />
                  )}
                  <span className={`font-medium ${testDialog.result.success ? 'text-green-800' : 'text-red-800'}`}>
                    {testDialog.result.success ? 'Connection Successful' : 'Connection Failed'}
                  </span>
                </div>
                <div>
                  <Label>Response Time</Label>
                  <div className="text-sm text-gray-600">{testDialog.result.responseTime}ms</div>
                </div>
                <div>
                  <Label>Status</Label>
                  <div className="text-sm text-gray-600">{testDialog.result.status || 'N/A'}</div>
                </div>
                {testDialog.result.error && (
                  <div>
                    <Label>Error Details</Label>
                    <Textarea
                      value={testDialog.result.error}
                      readOnly
                      className="text-sm"
                      rows={3}
                    />
                  </div>
                )}
                {testDialog.result.data && (
                  <div>
                    <Label>Response Data</Label>
                    <Textarea
                      value={JSON.stringify(testDialog.result.data, null, 2)}
                      readOnly
                      className="text-sm font-mono"
                      rows={4}
                    />
                  </div>
                )}
              </>
            ) : (
              <div className="text-center py-4">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mx-auto"></div>
                <div className="mt-2 text-sm text-gray-600">Testing connection...</div>
              </div>
            )}
            <div className="flex justify-end">
              <Button onClick={() => setTestDialog({open: false, integration: null, result: null})}>
                Close
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

