    },
    {
      id: 'dealer-456',
      name: 'Metro Vehicle Services',
      email: 'sales@metrovehicle.com',
      phone: '(555) 987-6543',
      status: 'active',
      commissionRate: 12.5,
      totalSales: 189000,
      policiesSold: 98,
      joinDate: '2024-04-22',
      lastSale: '2025-08-09',
      tier: 'gold'
    },
    {
      id: 'dealer-789',
      name: 'Coastal Auto Care',
      email: 'info@coastalauto.com',
      phone: '(555) 456-7890',
      status: 'active',
      commissionRate: 10.0,
      totalSales: 95000,
      policiesSold: 67,
      joinDate: '2024-06-10',
      lastSale: '2025-08-08',
      tier: 'silver'
    },
    {
      id: 'dealer-101',
      name: 'Budget Car Solutions',
      email: 'team@budgetcar.com',
      phone: '(555) 321-0987',
      status: 'pending',
