import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  FileText, 
  Upload, 
  Eye, 
  MessageSquare, 
  Clock, 
  CheckCircle,
  AlertTriangle,
  XCircle,
  DollarSign,
  Calendar,
  Camera,
  Phone,
  Mail,
  MapPin,
  User,
  Car,
  Home,
  Search,
  Filter,
  Download,
  RefreshCw,
  Bot,
  TrendingUp,
  AlertCircle,
  CheckCircle2,
  Users
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { format } from "date-fns";

interface Claim {
  id: string;
  claimNumber: string;
  policyNumber: string;
  customerName: string;
  customerEmail: string;
  customerPhone: string;
  dateOfLoss: string;
  reportedDate: string;
  claimType: "auto" | "home" | "rv" | "marine" | "powersports";
  status: "submitted" | "under_review" | "investigating" | "approved" | "denied" | "closed";
  priority: "low" | "medium" | "high" | "critical";
  estimatedAmount: number;
  approvedAmount?: number;
  description: string;
  adjusterName?: string;
  adjusterEmail?: string;
  lastUpdate: string;
  documents: string[];
  aiAnalysis?: {
    riskScore: number;
    fraudIndicators: string[];
    recommendations: string[];
    estimatedProcessingTime: number;
  };
  timeline: {
    date: string;
    action: string;
    user: string;
    notes?: string;
  }[];
}

const statusColors = {
  submitted: "bg-blue-100 text-blue-800",
  under_review: "bg-yellow-100 text-yellow-800", 
  investigating: "bg-orange-100 text-orange-800",
  approved: "bg-green-100 text-green-800",
  denied: "bg-red-100 text-red-800",
  closed: "bg-gray-100 text-gray-800"
};

const priorityColors = {
  low: "bg-green-100 text-green-800",
  medium: "bg-yellow-100 text-yellow-800",
  high: "bg-orange-100 text-orange-800",
  critical: "bg-red-100 text-red-800"
};

export default function AdvancedClaims() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedClaim, setSelectedClaim] = useState<Claim | null>(null);
  const [filterStatus, setFilterStatus] = useState<string>("all");
  const [filterType, setFilterType] = useState<string>("all");
  const [searchTerm, setSearchTerm] = useState("");

  // Fetch claims with filters
  const { data: claims = [], isLoading } = useQuery({
    queryKey: ["/api/claims/advanced", filterStatus, filterType, searchTerm],
  });

  // Fetch claim statistics
  const { data: claimStats } = useQuery({
    queryKey: ["/api/claims/statistics"],
  });

  // AI Analysis mutation
  const aiAnalysisMutation = useMutation({
    mutationFn: async (claimId: string) => {
      return await apiRequest("POST", `/api/claims/${claimId}/ai-analysis`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/claims/advanced"] });
      toast({
        title: "AI Analysis Complete",
        description: "Fraud detection and risk assessment completed",
      });
    },
  });

  // Update claim status
  const updateStatusMutation = useMutation({
    mutationFn: async ({ claimId, status, notes }: { claimId: string; status: string; notes?: string }) => {
      return await apiRequest("PUT", `/api/claims/${claimId}/status`, { status, notes });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/claims/advanced"] });
      toast({
        title: "Status Updated",
        description: "Claim status has been successfully updated",
      });
    },
  });

  // Assign adjuster
  const assignAdjusterMutation = useMutation({
    mutationFn: async ({ claimId, adjusterData }: { claimId: string; adjusterData: any }) => {
      return await apiRequest("PUT", `/api/claims/${claimId}/adjuster`, adjusterData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/claims/advanced"] });
      toast({
        title: "Adjuster Assigned",
        description: "Claim has been assigned to adjuster successfully",
      });
    },
  });

  const filteredClaims = claims.filter((claim: Claim) => {
    const matchesStatus = filterStatus === "all" || claim.status === filterStatus;
    const matchesType = filterType === "all" || claim.claimType === filterType;
    const matchesSearch = searchTerm === "" || 
      claim.claimNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
      claim.customerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      claim.policyNumber.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesStatus && matchesType && matchesSearch;
  });

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Advanced Claims Management</h1>
            <p className="text-gray-600 mt-2">AI-powered claims processing and fraud detection</p>
          </div>
          <div className="flex space-x-4">
            <Button variant="outline" data-testid="button-export-claims">
              <Download className="h-4 w-4 mr-2" />
              Export Claims
            </Button>
            <Button data-testid="button-new-claim">
              <FileText className="h-4 w-4 mr-2" />
              New Claim
            </Button>
          </div>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Claims</CardTitle>
              <FileText className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-total-claims">
                {claimStats?.totalClaims || 0}
              </div>
              <p className="text-xs text-muted-foreground">
                +{claimStats?.newThisMonth || 0} this month
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Avg Processing Time</CardTitle>
              <Clock className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-avg-processing">
                {claimStats?.avgProcessingDays || 0} days
              </div>
              <p className="text-xs text-muted-foreground">
                -2 days from last month
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Payouts</CardTitle>
              <DollarSign className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-total-payouts">
                ${(claimStats?.totalPayouts || 0).toLocaleString()}
              </div>
              <p className="text-xs text-muted-foreground">
                +12% from last month
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Fraud Detection</CardTitle>
              <AlertTriangle className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-fraud-rate">
                {claimStats?.fraudRate || 0}%
              </div>
              <p className="text-xs text-muted-foreground">
                AI-powered detection
              </p>
            </CardContent>
          </Card>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-lg shadow-sm border p-6 mb-6">
          <div className="flex flex-wrap items-center gap-4">
            <div className="flex-1 min-w-[300px]">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                <Input
                  placeholder="Search by claim number, customer name, or policy..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                  data-testid="input-search-claims"
                />
              </div>
            </div>

            <Select value={filterStatus} onValueChange={setFilterStatus}>
              <SelectTrigger className="w-[180px]" data-testid="select-status-filter">
                <SelectValue placeholder="Filter by Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Statuses</SelectItem>
                <SelectItem value="submitted">Submitted</SelectItem>
                <SelectItem value="under_review">Under Review</SelectItem>
                <SelectItem value="investigating">Investigating</SelectItem>
                <SelectItem value="approved">Approved</SelectItem>
                <SelectItem value="denied">Denied</SelectItem>
                <SelectItem value="closed">Closed</SelectItem>
              </SelectContent>
            </Select>

            <Select value={filterType} onValueChange={setFilterType}>
              <SelectTrigger className="w-[180px]" data-testid="select-type-filter">
                <SelectValue placeholder="Filter by Type" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Types</SelectItem>
                <SelectItem value="auto">Auto</SelectItem>
                <SelectItem value="home">Home</SelectItem>
                <SelectItem value="rv">RV</SelectItem>
                <SelectItem value="marine">Marine</SelectItem>
                <SelectItem value="powersports">Powersports</SelectItem>
              </SelectContent>
            </Select>

            <Button variant="outline" data-testid="button-refresh-claims">
              <RefreshCw className="h-4 w-4" />
            </Button>
          </div>
        </div>

        <div className="grid lg:grid-cols-3 gap-6">
          {/* Claims List */}
          <div className="lg:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle>Claims ({filteredClaims.length})</CardTitle>
              </CardHeader>
              <CardContent className="p-0">
                {isLoading ? (
                  <div className="p-6 text-center">
                    <RefreshCw className="h-8 w-8 animate-spin mx-auto mb-2" />
                    <p>Loading claims...</p>
                  </div>
                ) : filteredClaims.length === 0 ? (
                  <div className="p-6 text-center text-gray-500">
                    No claims found matching your criteria
                  </div>
                ) : (
                  <div className="divide-y">
                    {filteredClaims.map((claim: Claim) => (
                      <div
                        key={claim.id}
                        className="p-4 hover:bg-gray-50 cursor-pointer"
                        onClick={() => setSelectedClaim(claim)}
                        data-testid={`claim-item-${claim.id}`}
                      >
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <div className="flex items-center space-x-2 mb-2">
                              <span className="font-medium text-sm">
                                {claim.claimNumber}
                              </span>
                              <Badge className={statusColors[claim.status]}>
                                {claim.status.replace('_', ' ')}
                              </Badge>
                              <Badge className={priorityColors[claim.priority]}>
                                {claim.priority}
                              </Badge>
                              {claim.aiAnalysis && claim.aiAnalysis.riskScore > 70 && (
                                <Badge className="bg-red-100 text-red-800">
                                  <AlertTriangle className="h-3 w-3 mr-1" />
                                  High Risk
                                </Badge>
                              )}
                            </div>
                            <p className="text-sm text-gray-900 font-medium">
                              {claim.customerName}
                            </p>
                            <p className="text-sm text-gray-600">
                              Policy: {claim.policyNumber} ‚Ä¢ {claim.claimType.toUpperCase()}
                            </p>
                            <p className="text-sm text-gray-500">
                              Loss Date: {format(new Date(claim.dateOfLoss), 'MMM dd, yyyy')}
                            </p>
                          </div>
                          <div className="text-right">
                            <p className="text-sm font-medium">
                              ${claim.estimatedAmount.toLocaleString()}
                            </p>
                            <p className="text-xs text-gray-500">
                              {format(new Date(claim.lastUpdate), 'MMM dd')}
                            </p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Claim Details */}
          <div className="lg:col-span-1">
            {selectedClaim ? (
              <Card>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg">Claim Details</CardTitle>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => aiAnalysisMutation.mutate(selectedClaim.id)}
                      disabled={aiAnalysisMutation.isPending}
                      data-testid="button-ai-analysis"
                    >
                      {aiAnalysisMutation.isPending ? (
                        <RefreshCw className="h-4 w-4 animate-spin mr-2" />
                      ) : (
                        <Bot className="h-4 w-4 mr-2" />
                      )}
                      AI Analysis
                    </Button>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  <Tabs defaultValue="details" className="w-full">
                    <TabsList className="grid w-full grid-cols-3">
                      <TabsTrigger value="details">Details</TabsTrigger>
                      <TabsTrigger value="analysis">AI Analysis</TabsTrigger>
                      <TabsTrigger value="timeline">Timeline</TabsTrigger>
                    </TabsList>

                    <TabsContent value="details" className="space-y-4">
                      <div>
                        <Label className="text-sm font-medium">Claim Number</Label>
                        <p className="text-sm text-gray-600" data-testid="text-claim-number">
                          {selectedClaim.claimNumber}
                        </p>
                      </div>

                      <div>
                        <Label className="text-sm font-medium">Customer</Label>
                        <div className="space-y-1">
                          <p className="text-sm text-gray-600">
                            <User className="h-3 w-3 inline mr-1" />
                            {selectedClaim.customerName}
                          </p>
                          <p className="text-sm text-gray-600">
                            <Mail className="h-3 w-3 inline mr-1" />
                            {selectedClaim.customerEmail}
                          </p>
                          <p className="text-sm text-gray-600">
                            <Phone className="h-3 w-3 inline mr-1" />
                            {selectedClaim.customerPhone}
                          </p>
                        </div>
                      </div>

                      <div>
                        <Label className="text-sm font-medium">Description</Label>
                        <p className="text-sm text-gray-600">
                          {selectedClaim.description}
                        </p>
                      </div>

                      <div className="flex space-x-4">
                        <div className="flex-1">
                          <Label className="text-sm font-medium">Estimated</Label>
                          <p className="text-lg font-semibold text-green-600">
                            ${selectedClaim.estimatedAmount.toLocaleString()}
                          </p>
                        </div>
                        {selectedClaim.approvedAmount && (
                          <div className="flex-1">
                            <Label className="text-sm font-medium">Approved</Label>
                            <p className="text-lg font-semibold text-blue-600">
                              ${selectedClaim.approvedAmount.toLocaleString()}
                            </p>
                          </div>
                        )}
                      </div>

                      <div className="space-y-2">
                        <Label className="text-sm font-medium">Update Status</Label>
                        <Select
                          value={selectedClaim.status}
                          onValueChange={(status) => 
                            updateStatusMutation.mutate({ 
                              claimId: selectedClaim.id, 
                              status 
                            })
                          }
                        >
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="submitted">Submitted</SelectItem>
                            <SelectItem value="under_review">Under Review</SelectItem>
                            <SelectItem value="investigating">Investigating</SelectItem>
                            <SelectItem value="approved">Approved</SelectItem>
                            <SelectItem value="denied">Denied</SelectItem>
                            <SelectItem value="closed">Closed</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                    </TabsContent>

                    <TabsContent value="analysis" className="space-y-4">
                      {selectedClaim.aiAnalysis ? (
                        <>
                          <div>
                            <Label className="text-sm font-medium">Risk Score</Label>
                            <div className="flex items-center space-x-2">
                              <div className="flex-1 bg-gray-200 rounded-full h-2">
                                <div 
                                  className={`h-2 rounded-full ${
                                    selectedClaim.aiAnalysis.riskScore > 70 ? 'bg-red-500' :
                                    selectedClaim.aiAnalysis.riskScore > 40 ? 'bg-yellow-500' : 'bg-green-500'
                                  }`}
                                  style={{ width: `${selectedClaim.aiAnalysis.riskScore}%` }}
                                />
                              </div>
                              <span className="text-sm font-medium">
                                {selectedClaim.aiAnalysis.riskScore}%
                              </span>
                            </div>
                          </div>

                          <div>
                            <Label className="text-sm font-medium">Fraud Indicators</Label>
                            <div className="space-y-1">
                              {selectedClaim.aiAnalysis.fraudIndicators.map((indicator, index) => (
                                <div key={index} className="flex items-center space-x-2">
                                  <AlertTriangle className="h-3 w-3 text-red-500" />
                                  <span className="text-sm text-red-600">{indicator}</span>
                                </div>
                              ))}
                            </div>
                          </div>

                          <div>
                            <Label className="text-sm font-medium">Recommendations</Label>
                            <div className="space-y-1">
                              {selectedClaim.aiAnalysis.recommendations.map((rec, index) => (
                                <div key={index} className="flex items-center space-x-2">
                                  <CheckCircle2 className="h-3 w-3 text-blue-500" />
                                  <span className="text-sm text-blue-600">{rec}</span>
                                </div>
                              ))}
                            </div>
                          </div>

                          <div>
                            <Label className="text-sm font-medium">Est. Processing Time</Label>
                            <p className="text-sm text-gray-600">
                              {selectedClaim.aiAnalysis.estimatedProcessingTime} business days
                            </p>
                          </div>
                        </>
                      ) : (
                        <div className="text-center py-8">
                          <Bot className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                          <p className="text-gray-500">No AI analysis available</p>
                          <Button
                            className="mt-4"
                            onClick={() => aiAnalysisMutation.mutate(selectedClaim.id)}
                            disabled={aiAnalysisMutation.isPending}
                          >
                            Run AI Analysis
                          </Button>
                        </div>
                      )}
                    </TabsContent>

                    <TabsContent value="timeline" className="space-y-4">
                      <div className="space-y-4">
                        {selectedClaim.timeline.map((event, index) => (
                          <div key={index} className="flex space-x-3">
                            <div className="flex-shrink-0">
                              <div className="w-2 h-2 bg-blue-500 rounded-full mt-2"></div>
                            </div>
                            <div className="flex-1">
                              <p className="text-sm font-medium">{event.action}</p>
                              <p className="text-xs text-gray-500">
                                {format(new Date(event.date), 'MMM dd, yyyy HH:mm')} by {event.user}
                              </p>
                              {event.notes && (
                                <p className="text-sm text-gray-600 mt-1">{event.notes}</p>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    </TabsContent>
                  </Tabs>
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardContent className="text-center py-12">
                  <FileText className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-500">Select a claim to view details</p>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/BrandedQuotePage.tsx.content:
import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';

interface BrandedQuotePageProps {
  resellerId: string;
  config?: any;
}

export default function BrandedQuotePage({ resellerId, config }: BrandedQuotePageProps) {
  const [vin, setVin] = useState('');
  const [mileage, setMileage] = useState('');
  const [customerEmail, setCustomerEmail] = useState('');
  const [customerName, setCustomerName] = useState('');
  const [selectedProduct, setSelectedProduct] = useState('');

  const { data: whitelabelConfig } = useQuery({
    queryKey: [`/api/wholesale/white-label/config/${resellerId}`],
    enabled: !config,
  });

  const brandConfig = config || whitelabelConfig;

  // Apply custom branding
  useEffect(() => {
    if (brandConfig?.branding) {
      const root = document.documentElement;
      if (brandConfig.branding.primaryColor) {
        root.style.setProperty('--primary', brandConfig.branding.primaryColor);
      }
      if (brandConfig.branding.secondaryColor) {
        root.style.setProperty('--secondary', brandConfig.branding.secondaryColor);
      }
    }
  }, [brandConfig]);

  const handleSubmitQuote = () => {
    // Submit quote logic here
    console.log('Quote submitted:', {
      resellerId,
      vin,
      mileage,
      customerEmail,
      customerName,
      selectedProduct,
    });
  };

  if (!brandConfig) {
    return <div className="p-6">Loading...</div>;
  }

  const { branding, products } = brandConfig;
  const availableProducts = Object.entries(products || {})
    .filter(([_, product]: [string, any]) => product.enabled)
    .map(([key, product]: [string, any]) => ({
      key,
      name: key === 'autoAdvantage' ? 'Auto Advantage Program' :
            key === 'homeProtection' ? 'Home Protection Plan' :
            key === 'allVehicle' ? 'All-Vehicle Protection' : key,
      product
    }));

  return (
    <div 
      className="min-h-screen"
      style={{ 
        backgroundColor: 'var(--background)',
        fontFamily: 'var(--font-family, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif)'
      }}
    >
      {/* Header */}
      <header className="border-b" style={{ backgroundColor: 'var(--card)' }}>
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            {branding?.logoUrl && (
              <img 
                src={branding.logoUrl} 
                alt={branding.companyName || 'Company Logo'}
                className="h-10"
                data-testid="branded-logo"
              />
            )}
            <div className="text-right">
              {branding?.companyName && (
                <h1 className="text-2xl font-bold" style={{ color: branding.primaryColor }}>
                  {branding.companyName}
                </h1>
              )}
              {branding?.tagline && (
                <p className="text-muted-foreground">{branding.tagline}</p>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto">
          {/* Hero Section */}
          <div className="text-center mb-8">
            <h2 className="text-3xl font-bold mb-4">
              {brandConfig.pages?.landingPage?.title || 'Get Your Protection Quote'}
            </h2>
            <p className="text-lg text-muted-foreground mb-6">
              {brandConfig.pages?.landingPage?.heroText || 'Protect your vehicle with comprehensive coverage'}
            </p>
          </div>

          {/* Quote Form */}
          <Card>
            <CardHeader>
              <CardTitle>Get Your Free Quote</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="customer-name">Full Name *</Label>
                  <Input
                    id="customer-name"
                    value={customerName}
                    onChange={(e) => setCustomerName(e.target.value)}
                    placeholder="Enter your full name"
                    required
                    data-testid="input-customer-name"
                  />
                </div>
                <div>
                  <Label htmlFor="customer-email">Email Address *</Label>
                  <Input
                    id="customer-email"
                    type="email"
                    value={customerEmail}
                    onChange={(e) => setCustomerEmail(e.target.value)}
                    placeholder="your@email.com"
                    required
                    data-testid="input-customer-email"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="product-select">Protection Plan *</Label>
                <Select value={selectedProduct} onValueChange={setSelectedProduct}>
                  <SelectTrigger data-testid="select-product">
                    <SelectValue placeholder="Select a protection plan" />
                  </SelectTrigger>
                  <SelectContent>
                    {availableProducts.map(({ key, name }) => (
                      <SelectItem key={key} value={key}>
                        {name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="vin-input">Vehicle VIN</Label>
                  <Input
                    id="vin-input"
                    value={vin}
                    onChange={(e) => setVin(e.target.value.toUpperCase())}
                    placeholder="Enter 17-character VIN"
                    maxLength={17}
                    data-testid="input-vin"
                  />
                  <p className="text-sm text-muted-foreground mt-1">
                    Found on dashboard, driver's door frame, or registration
                  </p>
                </div>
                <div>
                  <Label htmlFor="mileage-input">Current Mileage</Label>
                  <Input
                    id="mileage-input"
                    type="number"
                    value={mileage}
                    onChange={(e) => setMileage(e.target.value)}
                    placeholder="Enter current miles"
                    data-testid="input-mileage"
                  />
                </div>
              </div>

              <Button 
                className="w-full" 
                size="lg"
                onClick={handleSubmitQuote}
                style={{ 
                  backgroundColor: branding?.primaryColor || 'hsl(var(--primary))',
                  borderColor: branding?.primaryColor || 'hsl(var(--primary))'
                }}
                disabled={!customerName || !customerEmail || !selectedProduct}
                data-testid="button-get-quote"
              >
                {brandConfig.pages?.landingPage?.ctaText || 'Get My Free Quote'}
              </Button>

              <p className="text-sm text-muted-foreground text-center">
                No obligation ‚Ä¢ Instant results ‚Ä¢ Secure & confidential
              </p>
            </CardContent>
          </Card>

          {/* Features */}
          {brandConfig.pages?.landingPage?.features && (
            <div className="mt-8">
              <h3 className="text-xl font-semibold text-center mb-6">Why Choose Us?</h3>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                {brandConfig.pages.landingPage.features.map((feature: string, index: number) => (
                  <div key={index} className="text-center p-4">
                    <div 
                      className="w-12 h-12 rounded-full mx-auto mb-2 flex items-center justify-center"
                      style={{ backgroundColor: `${branding?.primaryColor || '#2563eb'}20` }}
                    >
                      <span 
                        className="text-lg font-bold"
                        style={{ color: branding?.primaryColor || '#2563eb' }}
                      >
                        ‚úì
                      </span>
                    </div>
                    <p className="text-sm font-medium">{feature}</p>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </main>

      {/* Footer */}
      <footer className="border-t mt-16" style={{ backgroundColor: 'var(--card)' }}>
        <div className="container mx-auto px-4 py-6">
          <div className="text-center text-sm text-muted-foreground">
            {branding?.contactPhone && (
              <p className="mb-2">
                Call us: <a href={`tel:${branding.contactPhone}`} className="text-primary hover:underline">
                  {branding.contactPhone}
                </a>
              </p>
            )}
            {branding?.contactEmail && (
              <p className="mb-2">
                Email: <a href={`mailto:${branding.contactEmail}`} className="text-primary hover:underline">
                  {branding.contactEmail}
                </a>
              </p>
            )}
            <p>¬© 2025 {branding?.companyName || 'Insurance Partners'}. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

client/src/pages/Claims.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel } from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { AlertTriangle, Search, Plus, Shield, Upload, X, FileText, Image } from "lucide-react";
import { Link } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

// Form schema for new claims
const claimFormSchema = z.object({
  policyNumber: z.string().min(1, "Policy/Contract number is required"),
  claimantName: z.string().min(1, "Claimant name is required"),
  claimantEmail: z.string().email("Valid email is required"),
  claimantPhone: z.string().optional(),
  type: z.enum(["mechanical_breakdown", "deductible_reimbursement", "tire_wheel", "key_replacement", "theft", "ding_dent"]),
  dateOfLoss: z.string().min(1, "Date of loss is required"),
  description: z.string().min(10, "Description must be at least 10 characters"),
  estimatedAmount: z.string().optional(),
});

export default function Claims() {
  const [searchTerm, setSearchTerm] = useState("");
  const [showNewClaimModal, setShowNewClaimModal] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: claims, isLoading } = useQuery({
    queryKey: ["/api/claims"],
    retry: false,
  });

  const form = useForm<z.infer<typeof claimFormSchema>>({
    resolver: zodResolver(claimFormSchema),
    defaultValues: {
      policyNumber: "",
      claimantName: "",
      claimantEmail: "",
      claimantPhone: "",
      type: "mechanical_breakdown",
      dateOfLoss: "",
      description: "",
      estimatedAmount: "",
    },
  });

  const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);

  const createClaimMutation = useMutation({
    mutationFn: async (claimData: z.infer<typeof claimFormSchema>) => {
      // Convert form data to API format
      const apiData = {
        ...claimData,
        dateOfLoss: new Date(claimData.dateOfLoss),
        estimatedAmount: claimData.estimatedAmount ? parseFloat(claimData.estimatedAmount) : undefined,
        documents: uploadedFiles.map(file => ({
          name: file.name,
          type: file.type,
          size: file.size
        }))
      };
      return apiRequest("/api/claims", "POST", apiData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/claims"] });
      setShowNewClaimModal(false);
      form.reset();
      setUploadedFiles([]);
      toast({
        title: "Claim Created",
        description: "New claim has been successfully filed",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create claim",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: z.infer<typeof claimFormSchema>) => {
    createClaimMutation.mutate(data);
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || []);
    const validFiles = files.filter(file => {
      const isValidType = file.type.startsWith('image/') || file.type === 'application/pdf' || file.type.startsWith('text/');
      const isValidSize = file.size <= 10 * 1024 * 1024; // 10MB limit
      return isValidType && isValidSize;
    });

    setUploadedFiles(prev => [...prev, ...validFiles]);

    if (validFiles.length !== files.length) {
      toast({
        title: "Some files were skipped",
        description: "Only images, PDFs, and text files under 10MB are allowed",
        variant: "destructive",
      });
    }
  };

  const removeFile = (index: number) => {
    setUploadedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const getFileIcon = (file: File) => {
    if (file.type.startsWith('image/')) return <Image className="h-4 w-4" />;
    return <FileText className="h-4 w-4" />;
  };

  const filteredClaims = Array.isArray(claims) ? claims.filter((claim: any) =>
    claim.claimNumber?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    claim.description?.toLowerCase().includes(searchTerm.toLowerCase())
  ) : [];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'open': return 'default';
      case 'review': return 'secondary';
      case 'awaiting_docs': return 'secondary';
      case 'estimate': return 'secondary';
      case 'decision': return 'secondary';
      case 'approved': return 'default';
      case 'denied': return 'destructive';
      case 'payout': return 'default';
      case 'closed': return 'outline';
      default: return 'outline';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Link href="/">
                <Shield className="h-8 w-8 text-blue-600" />
              </Link>
              <h1 className="text-2xl font-bold text-gray-900">Claims Management</h1>
            </div>
            <Button onClick={() => setShowNewClaimModal(true)}>
              <Plus className="h-4 w-4 mr-2" />
              File New Claim
            </Button>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white border-b">
        <div className="container mx-auto px-4">
          <div className="flex space-x-8">
            <Link href="/" className="py-4 px-2 border-b-2 border-transparent text-gray-500 hover:text-gray-700">
              Dashboard
            </Link>
            <Link href="/policies" className="py-4 px-2 border-b-2 border-transparent text-gray-500 hover:text-gray-700">
              Policies
            </Link>
            <Link href="/claims" className="py-4 px-2 border-b-2 border-blue-500 text-blue-600 font-medium">
              Claims
            </Link>
            <Link href="/analytics" className="py-4 px-2 border-b-2 border-transparent text-gray-500 hover:text-gray-700">
              Analytics
            </Link>
            <Link href="/admin" className="py-4 px-2 border-b-2 border-transparent text-gray-500 hover:text-gray-700">
              Admin
            </Link>
          </div>
        </div>
      </nav>

      <main className="container mx-auto px-4 py-8">
        {/* Search */}
        <div className="mb-6">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
            <Input
              placeholder="Search claims by number or description..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>
        </div>

        {/* Claims List */}
        <div className="space-y-4">
          {isLoading ? (
            <Card>
              <CardContent className="py-8">
                <div className="text-center text-gray-500">Loading claims...</div>
              </CardContent>
            </Card>
          ) : filteredClaims.length > 0 ? (
            filteredClaims.map((claim: any) => (
              <Card key={claim.id} className="hover:shadow-md transition-shadow">
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg">{claim.claimNumber}</CardTitle>
                    <Badge variant={getStatusColor(claim.status)}>
                      {claim.status?.replace('_', ' ')}
                    </Badge>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <p className="text-sm font-medium text-gray-500">Incident Date</p>
                      <p className="text-sm text-gray-900">
                        {claim.incidentDate ? new Date(claim.incidentDate).toLocaleDateString() : 'N/A'}
                      </p>
                    </div>
                    <div>
                      <p className="text-sm font-medium text-gray-500">Estimated Amount</p>
                      <p className="text-sm text-gray-900">
                        {claim.estimatedAmount ? `$${claim.estimatedAmount}` : 'Not assessed'}
                      </p>
                    </div>
                    <div>
                      <p className="text-sm font-medium text-gray-500">Submitted</p>
                      <p className="text-sm text-gray-900">
                        {claim.submittedAt ? new Date(claim.submittedAt).toLocaleDateString() : 'N/A'}
                      </p>
                    </div>
                  </div>
                  <div className="mt-4">
                    <p className="text-sm font-medium text-gray-500">Description</p>
                    <p className="text-sm text-gray-900 mt-1">{claim.description || 'No description provided'}</p>
                  </div>
                  <div className="mt-4 flex items-center justify-between">
                    <div className="text-sm text-gray-500">
                      Location: {claim.incidentLocation || 'Not specified'}
                    </div>
                    <div className="flex space-x-2">
                      <Button variant="outline" size="sm">
                        View Details
                      </Button>
                      <Button variant="outline" size="sm">
                        Process
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <Card>
              <CardContent className="py-8">
                <div className="text-center">
                  <AlertTriangle className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">
                    {searchTerm ? 'No claims found' : 'No claims yet'}
                  </h3>
                  <p className="text-gray-500 mb-4">
                    {searchTerm 
                      ? 'Try adjusting your search criteria' 
                      : 'Claims will appear here when customers file them'
                    }
                  </p>
                  <Button onClick={() => setShowNewClaimModal(true)}>
                    <Plus className="h-4 w-4 mr-2" />
                    File New Claim
                  </Button>
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      </main>

      {/* New Claim Modal */}
      <Dialog open={showNewClaimModal} onOpenChange={setShowNewClaimModal}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>File New Claim</DialogTitle>
          </DialogHeader>

          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <FormField
                  control={form.control}
                  name="policyNumber"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Policy/Contract Number *</FormLabel>
                      <FormControl>
                        <Input {...field} placeholder="Enter policy or contract number" />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="claimantName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Claimant Name *</FormLabel>
                      <FormControl>
                        <Input {...field} placeholder="Enter claimant's full name" />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="claimantEmail"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email *</FormLabel>
                      <FormControl>
                        <Input {...field} type="email" placeholder="Enter email address" />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="claimantPhone"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Phone Number</FormLabel>
                      <FormControl>
                        <Input {...field} placeholder="(555) 123-4567" />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="type"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Claim Type *</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select claim type" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="mechanical_breakdown">Mechanical Breakdown</SelectItem>
                          <SelectItem value="deductible_reimbursement">Deductible Reimbursement</SelectItem>
                          <SelectItem value="tire_wheel">Tire & Wheel</SelectItem>
                          <SelectItem value="key_replacement">Key Replacement</SelectItem>
                          <SelectItem value="theft">Theft</SelectItem>
                          <SelectItem value="ding_dent">Ding & Dent</SelectItem>
                        </SelectContent>
                      </Select>
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="dateOfLoss"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Date of Loss *</FormLabel>
                      <FormControl>
                        <Input {...field} type="date" />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="estimatedAmount"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Estimated Amount</FormLabel>
                      <FormControl>
                        <Input {...field} type="number" step="0.01" placeholder="0.00" />
                      </FormControl>
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Description *</FormLabel>
                    <FormControl>
                      <Textarea 
                        {...field} 
                        placeholder="Provide a detailed description of the incident"
                        className="min-h-[100px]"
                      />
                    </FormControl>
                  </FormItem>
                )}
              />

              {/* File Upload Section */}
              <div className="space-y-4">
                <div>
                  <FormLabel>Documents & Photos</FormLabel>
                  <p className="text-sm text-gray-500">Upload relevant documents, photos, or receipts (max 10MB each)</p>
                </div>

                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6">
                  <div className="text-center">
                    <Upload className="mx-auto h-12 w-12 text-gray-400" />
                    <div className="mt-4">
                      <label htmlFor="file-upload" className="cursor-pointer">
                        <span className="mt-2 block text-sm font-medium text-gray-900">
                          Drop files here or click to upload
                        </span>
                        <span className="mt-1 block text-xs text-gray-500">
                          PNG, JPG, PDF up to 10MB each
                        </span>
                      </label>
                      <input
                        id="file-upload"
                        name="file-upload"
                        type="file"
                        multiple
                        accept="image/*,.pdf,.txt"
                        className="sr-only"
                        onChange={handleFileUpload}
                      />
                    </div>
                  </div>
                </div>

                {/* Uploaded Files List */}
                {uploadedFiles.length > 0 && (
                  <div className="space-y-2">
                    <FormLabel>Uploaded Files ({uploadedFiles.length})</FormLabel>
                    <div className="space-y-2">
                      {uploadedFiles.map((file, index) => (
                        <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                          <div className="flex items-center space-x-3">
                            {getFileIcon(file)}
                            <div>
                              <p className="text-sm font-medium text-gray-900">{file.name}</p>
                              <p className="text-xs text-gray-500">
                                {(file.size / 1024 / 1024).toFixed(2)} MB
                              </p>
                            </div>
                          </div>
                          <Button
                            type="button"
                            variant="ghost"
                            size="sm"
                            onClick={() => removeFile(index)}
                          >
                            <X className="h-4 w-4" />
                          </Button>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              <div className="flex justify-end space-x-3">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setShowNewClaimModal(false)}
                >
                  Cancel
                </Button>
                <Button 
                  type="submit" 
                  disabled={createClaimMutation.isPending}
                >
                  {createClaimMutation.isPending ? "Creating..." : "Create Claim"}
                </Button>
              </div>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

