                  <Button 
                    type="submit" 
                    className="w-full bg-red-600 hover:bg-red-700 text-xl font-bold h-16" 
                    size="lg"
                    disabled={isProcessing || vinInput.length < 17 || !mileageInput || !nameInput.trim() || !emailInput.trim() || !zipcodeInput.trim()}
                    data-testid="button-get-quote"
                  >
                    {isProcessing ? (
                      <>Processing...</>
                    ) : (
                      <>
                        <Search className="h-4 w-4 mr-2" />
üî• Get Complete VSC Quote (All 5 Fields Required)
                      </>
                    )}
                  </Button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/LoginTest.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Shield, Check, AlertCircle, User } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";

export default function LoginTest() {
  const [testResults, setTestResults] = useState<any>(null);
  const [isLoading, setIsLoading] = useState(false);
  const { user, isAuthenticated, isLoading: authLoading } = useAuth();

  const runLoginTest = async () => {
    setIsLoading(true);
    const results: any = {
      timestamp: new Date().toLocaleString(),
      steps: []
    };

    try {
      // Step 1: Test admin access endpoint
      results.steps.push("Testing admin access endpoint...");
      const loginResponse = await fetch('/api/auth/admin-access', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include'
      });

      const loginData = await loginResponse.json();
      results.steps.push(`Login Response: ${loginResponse.status} - ${loginData.message}`);

      if (loginData.success) {
        results.steps.push("‚úÖ Admin access successful");

        // Step 2: Test user endpoint
        await new Promise(resolve => setTimeout(resolve, 1000)); // Wait for session to propagate

        results.steps.push("Testing user authentication endpoint...");
        const userResponse = await fetch('/api/auth/user', {
          credentials: 'include'
        });

        if (userResponse.ok) {
          const userData = await userResponse.json();
          results.steps.push(`‚úÖ User data retrieved: ${userData.email}`);
          results.authenticationWorking = true;
        } else {
          results.steps.push(`‚ùå User endpoint failed: ${userResponse.status}`);
          results.authenticationWorking = false;
        }
      } else {
        results.steps.push("‚ùå Admin access failed");
        results.authenticationWorking = false;
      }
    } catch (error: any) {
      results.steps.push(`‚ùå Error: ${error?.message || 'Unknown error'}`);
      results.authenticationWorking = false;
    }

    setTestResults(results);
    setIsLoading(false);
  };

  const refreshPage = () => {
    window.location.reload();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-blue-800 p-4">
      <div className="container mx-auto max-w-4xl py-8">
        <div className="text-center mb-8">
          <Shield className="h-16 w-16 text-white mx-auto mb-4" />
          <h1 className="text-4xl font-bold text-white mb-2">Authentication Test Center</h1>
          <p className="text-blue-100">Test the Quick Sign In functionality</p>
        </div>

        <div className="grid md:grid-cols-2 gap-6">
          {/* Current Auth Status */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="h-5 w-5" />
                Current Authentication Status
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              {authLoading ? (
                <p className="text-gray-600">Loading authentication status...</p>
              ) : isAuthenticated ? (
                <div className="space-y-2">
                  <div className="flex items-center gap-2 text-green-600">
                    <Check className="h-4 w-4" />
                    <span className="font-semibold">Authenticated</span>
                  </div>
                  {user && (
                    <div className="bg-green-50 p-3 rounded">
                      <p className="text-sm"><strong>Email:</strong> {(user as any).email}</p>
                      <p className="text-sm"><strong>Name:</strong> {(user as any).firstName} {(user as any).lastName}</p>
                      <p className="text-sm"><strong>ID:</strong> {(user as any).id}</p>
                    </div>
                  )}
                </div>
              ) : (
                <div className="flex items-center gap-2 text-red-600">
                  <AlertCircle className="h-4 w-4" />
                  <span className="font-semibold">Not Authenticated</span>
                </div>
              )}

              <Button onClick={refreshPage} variant="outline" className="w-full">
                Refresh Status
              </Button>
            </CardContent>
          </Card>

          {/* Test Login */}
          <Card>
            <CardHeader>
              <CardTitle>Quick Sign In Test</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-gray-600">
                Test the authentication system to verify it's working properly.
              </p>

              <Button 
                onClick={runLoginTest} 
                disabled={isLoading}
                className="w-full bg-blue-600 hover:bg-blue-700"
              >
                {isLoading ? "Testing..." : "Run Authentication Test"}
              </Button>

              {testResults && (
                <div className="mt-4 p-4 bg-gray-50 rounded border">
                  <h3 className="font-semibold mb-2">Test Results ({testResults.timestamp})</h3>
                  <div className="space-y-1 text-sm">
                    {testResults.steps.map((step: string, index: number) => (
                      <div key={index} className="font-mono text-xs">
                        {step}
                      </div>
                    ))}
                  </div>

                  {testResults.authenticationWorking === true && (
                    <div className="mt-3 p-2 bg-green-100 text-green-800 rounded text-sm">
                      ‚úÖ Authentication system is working correctly!
                    </div>
                  )}

                  {testResults.authenticationWorking === false && (
                    <div className="mt-3 p-2 bg-red-100 text-red-800 rounded text-sm">
                      ‚ùå Authentication test failed. Check server logs.
                    </div>
                  )}
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Instructions */}
        <Card className="mt-6">
          <CardHeader>
            <CardTitle>Instructions</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3 text-sm">
              <p><strong>1. Test Authentication:</strong> Click "Run Authentication Test" to verify the login system works</p>
              <p><strong>2. Check Status:</strong> The left panel shows your current authentication status</p>
              <p><strong>3. OAuth Issues:</strong> If OAuth callback fails, this Quick Sign In provides immediate access</p>
              <p><strong>4. Access Platform:</strong> Once authenticated, you can access all TPA features</p>
            </div>

            <div className="mt-4 p-3 bg-blue-50 rounded">
              <p className="text-blue-800 text-sm">
                <strong>Platform Access:</strong> After successful authentication, visit the main dashboard or use the navigation to access policies, claims, analytics, and other TPA features.
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/NewLanding.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  Shield, 
  Car, 
  CheckCircle, 
  Phone, 
  Search,
  AlertCircle
} from "lucide-react";
import { useLocation } from "wouter";

export default function NewLanding() {

  const [, setLocation] = useLocation();
  const [vinInput, setVinInput] = useState("");
  const [vinError, setVinError] = useState("");
  const [mileageInput, setMileageInput] = useState("");
  const [mileageError, setMileageError] = useState("");
  const [nameInput, setNameInput] = useState("");
  const [nameError, setNameError] = useState("");
  const [emailInput, setEmailInput] = useState("");
  const [emailError, setEmailError] = useState("");
  const [zipcodeInput, setZipcodeInput] = useState("");
  const [zipcodeError, setZipcodeError] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);

  const handleQuoteSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Validate all fields
    let hasErrors = false;

    if (!vinInput || vinInput.length !== 17) {
      setVinError("VIN must be exactly 17 characters");
      hasErrors = true;
    }

    if (!mileageInput) {
      setMileageError("Current mileage is required");
      hasErrors = true;
    }

    if (!nameInput.trim()) {
      setNameError("Full name is required");
      hasErrors = true;
    }

    if (!emailInput.trim()) {
      setEmailError("Email address is required");
      hasErrors = true;
    }

    if (!zipcodeInput.trim()) {
      setZipcodeError("ZIP code is required");
      hasErrors = true;
    }

    if (hasErrors) return;

    setIsProcessing(true);

    // Store form data in sessionStorage for the results page
    const formData = {
      vin: vinInput.toUpperCase(),
      mileage: parseInt(mileageInput),
      fullName: nameInput.trim(),
      email: emailInput.trim(),
      zipCode: zipcodeInput.trim()
    };

    console.log("üíæ NewLanding: Saving quote data to sessionStorage:", formData);
    sessionStorage.setItem('vscQuoteData', JSON.stringify(formData));

    // Verify data was saved
    const verification = sessionStorage.getItem('vscQuoteData');
    console.log("‚úÖ NewLanding: Verification - data saved:", verification);

    // Navigate to results page
    setTimeout(() => {
      console.log("üöÄ NewLanding: Navigating to /vsc-quote");
      setLocation('/vsc-quote');
    }, 500);
  };

  const handleLogin = () => {
    setLocation("/login");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-blue-800" style={{background: 'linear-gradient(135deg, #1e40af 0%, #1e3a8a 100%)'}}>
      {/* Navigation */}
      <nav className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-4">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <Shield className="h-8 w-8 text-blue-600 mr-3" />
              <span className="text-xl font-bold text-gray-900">Connected Auto Care</span>
            </div>
            <div className="flex items-center space-x-4">
              <Button variant="outline" onClick={handleLogin}>
                Sign In
              </Button>
            </div>
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <div className="relative py-16">
        <div className="container mx-auto px-4">
          <div className="max-w-3xl mx-auto">
            <div className="text-center text-white mb-8">
              <h1 className="text-4xl md:text-5xl font-bold mb-4">
                Vehicle Service Contract Protection
              </h1>
              <p className="text-lg mb-6">
                Comprehensive coverage for your vehicle with instant quotes and nationwide service
              </p>
            </div>

            {/* VSC Quote Form */}
            <div className="bg-white rounded-lg shadow-xl p-6 text-gray-900">
              <div className="text-center mb-6">
                <Car className="mx-auto h-12 w-12 text-blue-600 mb-3" />
                <h3 className="text-2xl font-bold mb-2 text-blue-600">Get Your Free Quote</h3>
                <p className="text-gray-700">Enter your information below for instant VSC pricing</p>
              </div>

              <div className="bg-gray-50 p-4 rounded-lg border shadow-sm">
                <form onSubmit={handleQuoteSubmit} className="space-y-4">
                  {/* Vehicle Information */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {/* VIN Input */}
                    <div className="space-y-2">
                      <label htmlFor="vin-input-new" className="block text-sm font-semibold text-gray-900">
                        Vehicle Identification Number (VIN) <span className="text-red-500">*</span>
                      </label>
                      <Input 
                        id="vin-input-new"
                        type="text"
                        placeholder="Enter 17-character VIN"
                        value={vinInput}
                        onChange={(e) => {
                          setVinInput(e.target.value.toUpperCase());
                          setVinError("");
                        }}
                        className="font-mono tracking-wide"
                        maxLength={17}
                        data-testid="input-vin-new"
                      />
                      {vinError && (
                        <Alert variant="destructive" className="py-2">
                          <AlertCircle className="h-4 w-4" />
                          <AlertDescription>{vinError}</AlertDescription>
                        </Alert>
                      )}
                    </div>

                    {/* Mileage Input */}
                    <div className="space-y-2">
                      <label htmlFor="mileage-input-new" className="block text-sm font-semibold text-gray-900">
                        Current Mileage <span className="text-red-500">*</span>
                      </label>
                      <Input 
                        id="mileage-input-new"
                        type="number"
                        placeholder="Enter current mileage"
                        value={mileageInput}
                        onChange={(e) => {
                          setMileageInput(e.target.value);
                          setMileageError("");
                        }}
                        min="0"
                        max="500000"
                        data-testid="input-mileage-new"
                      />
                      {mileageError && (
                        <Alert variant="destructive" className="py-2">
                          <AlertCircle className="h-4 w-4" />
                          <AlertDescription>{mileageError}</AlertDescription>
                        </Alert>
                      )}
                    </div>
                  </div>

                  {/* Contact Information */}
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {/* Full Name */}
                    <div className="space-y-2">
                      <label htmlFor="name-input-new" className="block text-sm font-semibold text-gray-900">
                        Full Name <span className="text-red-500">*</span>
                      </label>
                      <Input 
                        id="name-input-new"
                        type="text"
                        placeholder="Enter your full name"
                        value={nameInput}
                        onChange={(e) => {
                          setNameInput(e.target.value);
                          setNameError("");
                        }}
                        data-testid="input-name-new"
                      />
                      {nameError && (
                        <Alert variant="destructive" className="py-2">
                          <AlertCircle className="h-4 w-4" />
                          <AlertDescription>{nameError}</AlertDescription>
                        </Alert>
                      )}
                    </div>

                    {/* Email */}
                    <div className="space-y-2">
                      <label htmlFor="email-input-new" className="block text-sm font-semibold text-gray-900">
                        Email Address <span className="text-red-500">*</span>
                      </label>
                      <Input 
                        id="email-input-new"
                        type="email"
                        placeholder="Enter your email"
                        value={emailInput}
                        onChange={(e) => {
                          setEmailInput(e.target.value);
                          setEmailError("");
                        }}
                        data-testid="input-email-new"
                      />
                      {emailError && (
                        <Alert variant="destructive" className="py-2">
                          <AlertCircle className="h-4 w-4" />
                          <AlertDescription>{emailError}</AlertDescription>
                        </Alert>
                      )}
                    </div>

                    {/* ZIP Code */}
                    <div className="space-y-2">
                      <label htmlFor="zipcode-input-new" className="block text-sm font-semibold text-gray-900">
                        ZIP Code <span className="text-red-500">*</span>
                      </label>
                      <Input 
                        id="zipcode-input-new"
                        type="text"
                        placeholder="Enter ZIP code"
                        value={zipcodeInput}
                        onChange={(e) => {
                          setZipcodeInput(e.target.value);
                          setZipcodeError("");
                        }}
                        maxLength={10}
                        data-testid="input-zipcode-new"
                      />
                      {zipcodeError && (
                        <Alert variant="destructive" className="py-2">
                          <AlertCircle className="h-4 w-4" />
                          <AlertDescription>{zipcodeError}</AlertDescription>
                        </Alert>
                      )}
                    </div>
                  </div>

                  <div className="bg-blue-50 p-4 rounded-lg mt-4">
                    <h4 className="font-semibold text-blue-900 mb-2">What you'll get:</h4>
                    <ul className="space-y-1 text-sm text-blue-800">
                      <li className="flex items-center">
                        <CheckCircle className="h-4 w-4 mr-2 text-green-600" />
                        Instant vehicle information and eligibility
                      </li>
                      <li className="flex items-center">
                        <CheckCircle className="h-4 w-4 mr-2 text-green-600" />
                        Real-time pricing for all coverage levels
                      </li>
                      <li className="flex items-center">
                        <CheckCircle className="h-4 w-4 mr-2 text-green-600" />
                        Professional quote you can share or save
                      </li>
                    </ul>
                  </div>

                  <Button 
                    type="submit" 
                    className="w-full bg-blue-600 hover:bg-blue-700" 
                    size="lg"
                    disabled={isProcessing || vinInput.length < 17 || !mileageInput || !nameInput.trim() || !emailInput.trim() || !zipcodeInput.trim()}
                    data-testid="button-get-quote-new"
                  >
                    {isProcessing ? (
                      <>Processing...</>
                    ) : (
                      <>
                        <Search className="h-4 w-4 mr-2" />
                        Get Instant Quote
                      </>
                    )}
                  </Button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/PolicyManagement.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  FileText, 
  Download, 
  Eye, 
  Edit,
  MoreHorizontal,
  Calendar,
  DollarSign,
  Shield,
  Clock,
  CheckCircle,
  XCircle,
  AlertTriangle,
  RefreshCw,
  Search,
  Filter,
  Plus,
  Mail,
  Phone,
  Car,
  Home,
  MapPin,
  CreditCard,
  FileCheck,
  Send,
  Archive,
  Trash2,
  UserCheck
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { format, addDays, addMonths } from "date-fns";

interface Policy {
  id: string;
  policyNumber: string;
  customerName: string;
  customerEmail: string;
  customerPhone: string;
  productType: "auto" | "home" | "rv" | "marine" | "powersports";
  productName: string;
  coverageLevel: string;
  status: "active" | "pending" | "cancelled" | "expired" | "suspended";
  effectiveDate: string;
  expirationDate: string;
  premiumAmount: number;
  deductible: number;
  paymentMethod: string;
  paymentFrequency: "monthly" | "quarterly" | "semi-annual" | "annual";
  nextPaymentDue: string;
  agent?: string;
  notes: string;
  documents: {
    id: string;
    name: string;
    type: "policy" | "certificate" | "amendment" | "cancellation";
    url: string;
    createdAt: string;
  }[];
  claims: {
    id: string;
    claimNumber: string;
    dateOfLoss: string;
    status: string;
    amount: number;
  }[];
  vehicle?: {
    vin: string;
    year: number;
    make: string;
    model: string;
    mileage: number;
  };
  property?: {
    address: string;
    propertyType: string;
    squareFootage: number;
    yearBuilt: number;
  };
  renewalInfo: {
    autoRenew: boolean;
    renewalDate: string;
    renewalPremium: number;
    renewalNotificationSent: boolean;
  };
}

const statusColors = {
  active: "bg-green-100 text-green-800",
  pending: "bg-yellow-100 text-yellow-800",
  cancelled: "bg-red-100 text-red-800",
  expired: "bg-gray-100 text-gray-800",
  suspended: "bg-orange-100 text-orange-800"
};

const productTypeIcons = {
  auto: Car,
  home: Home,
  rv: Car,
  marine: Car,
  powersports: Car
};

export default function PolicyManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedPolicy, setSelectedPolicy] = useState<Policy | null>(null);
  const [filterStatus, setFilterStatus] = useState<string>("all");
  const [filterProduct, setFilterProduct] = useState<string>("all");
  const [searchTerm, setSearchTerm] = useState("");
  const [activeTab, setActiveTab] = useState("overview");

  // Fetch policies with filters
  const { data: policies = [], isLoading } = useQuery({
    queryKey: ["/api/policies/management", filterStatus, filterProduct, searchTerm],
  });

  // Fetch policy statistics
  const { data: policyStats } = useQuery({
    queryKey: ["/api/policies/statistics"],
  });

  // Generate document mutation
  const generateDocumentMutation = useMutation({
    mutationFn: async ({ policyId, documentType }: { policyId: string; documentType: string }) => {
      return await apiRequest("POST", `/api/policies/${policyId}/generate-document`, { documentType });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/policies/management"] });
      toast({
        title: "Document Generated",
        description: "Policy document has been generated successfully",
      });
    },
  });

  // Update policy status
  const updateStatusMutation = useMutation({
    mutationFn: async ({ policyId, status, reason }: { policyId: string; status: string; reason?: string }) => {
      return await apiRequest("PUT", `/api/policies/${policyId}/status`, { status, reason });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/policies/management"] });
      toast({
        title: "Status Updated",
        description: "Policy status has been successfully updated",
      });
    },
  });

  // Send renewal notice
  const sendRenewalMutation = useMutation({
    mutationFn: async (policyId: string) => {
      return await apiRequest("POST", `/api/policies/${policyId}/renewal-notice`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/policies/management"] });
      toast({
        title: "Renewal Notice Sent",
        description: "Renewal notice has been sent to the customer",
      });
    },
  });

  const filteredPolicies = policies.filter((policy: Policy) => {
    const matchesStatus = filterStatus === "all" || policy.status === filterStatus;
    const matchesProduct = filterProduct === "all" || policy.productType === filterProduct;
    const matchesSearch = searchTerm === "" || 
      policy.policyNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
      policy.customerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      policy.customerEmail.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesStatus && matchesProduct && matchesSearch;
  });

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'active': return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'pending': return <Clock className="h-4 w-4 text-yellow-500" />;
      case 'cancelled': return <XCircle className="h-4 w-4 text-red-500" />;
      case 'expired': return <Calendar className="h-4 w-4 text-gray-500" />;
      case 'suspended': return <AlertTriangle className="h-4 w-4 text-orange-500" />;
      default: return <FileText className="h-4 w-4" />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Policy Management</h1>
            <p className="text-gray-600 mt-2">Comprehensive policy lifecycle management and document generation</p>
          </div>
          <div className="flex space-x-4">
            <Button variant="outline" data-testid="button-export-policies">
              <Download className="h-4 w-4 mr-2" />
              Export Policies
            </Button>
            <Button data-testid="button-new-policy">
              <Plus className="h-4 w-4 mr-2" />
              Issue Policy
            </Button>
          </div>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Active Policies</CardTitle>
              <Shield className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-active-policies">
                {policyStats?.activePolicies || 0}
              </div>
              <p className="text-xs text-muted-foreground">
                +{policyStats?.newThisMonth || 0} this month
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Premium Revenue</CardTitle>
              <DollarSign className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-premium-revenue">
                ${(policyStats?.premiumRevenue || 0).toLocaleString()}
              </div>
              <p className="text-xs text-muted-foreground">
                +8% from last month
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Renewals Due</CardTitle>
              <Calendar className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-renewals-due">
                {policyStats?.renewalsDue || 0}
              </div>
              <p className="text-xs text-muted-foreground">
                Next 30 days
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Retention Rate</CardTitle>
              <UserCheck className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-retention-rate">
                {policyStats?.retentionRate || 0}%
              </div>
              <p className="text-xs text-muted-foreground">
                Customer retention
              </p>
            </CardContent>
          </Card>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-lg shadow-sm border p-6 mb-6">
          <div className="flex flex-wrap items-center gap-4">
            <div className="flex-1 min-w-[300px]">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                <Input
                  placeholder="Search by policy number, customer name, or email..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                  data-testid="input-search-policies"
                />
              </div>
            </div>

            <Select value={filterStatus} onValueChange={setFilterStatus}>
              <SelectTrigger className="w-[180px]" data-testid="select-status-filter">
                <SelectValue placeholder="Filter by Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Statuses</SelectItem>
                <SelectItem value="active">Active</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
                <SelectItem value="cancelled">Cancelled</SelectItem>
                <SelectItem value="expired">Expired</SelectItem>
                <SelectItem value="suspended">Suspended</SelectItem>
              </SelectContent>
            </Select>

            <Select value={filterProduct} onValueChange={setFilterProduct}>
              <SelectTrigger className="w-[180px]" data-testid="select-product-filter">
                <SelectValue placeholder="Filter by Product" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Products</SelectItem>
                <SelectItem value="auto">Auto</SelectItem>
                <SelectItem value="home">Home</SelectItem>
                <SelectItem value="rv">RV</SelectItem>
                <SelectItem value="marine">Marine</SelectItem>
                <SelectItem value="powersports">Powersports</SelectItem>
              </SelectContent>
            </Select>

            <Button variant="outline" data-testid="button-refresh-policies">
              <RefreshCw className="h-4 w-4" />
            </Button>
          </div>
        </div>

        <div className="grid lg:grid-cols-3 gap-6">
          {/* Policies List */}
          <div className="lg:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle>Policies ({filteredPolicies.length})</CardTitle>
              </CardHeader>
              <CardContent className="p-0">
                {isLoading ? (
                  <div className="p-6 text-center">
                    <RefreshCw className="h-8 w-8 animate-spin mx-auto mb-2" />
                    <p>Loading policies...</p>
                  </div>
                ) : filteredPolicies.length === 0 ? (
                  <div className="p-6 text-center text-gray-500">
                    No policies found matching your criteria
                  </div>
                ) : (
                  <div className="divide-y">
                    {filteredPolicies.map((policy: Policy) => {
                      const ProductIcon = productTypeIcons[policy.productType];
                      const isExpiringSoon = new Date(policy.expirationDate) < addDays(new Date(), 30);

                      return (
                        <div
                          key={policy.id}
                          className="p-4 hover:bg-gray-50 cursor-pointer"
                          onClick={() => setSelectedPolicy(policy)}
                          data-testid={`policy-item-${policy.id}`}
                        >
                          <div className="flex items-start justify-between">
                            <div className="flex-1">
                              <div className="flex items-center space-x-2 mb-2">
                                <ProductIcon className="h-4 w-4 text-gray-500" />
                                <span className="font-medium text-sm">
                                  {policy.policyNumber}
                                </span>
                                <Badge className={statusColors[policy.status]}>
                                  {policy.status}
                                </Badge>
                                {isExpiringSoon && policy.status === 'active' && (
                                  <Badge className="bg-orange-100 text-orange-800">
                                    <AlertTriangle className="h-3 w-3 mr-1" />
                                    Expiring Soon
                                  </Badge>
                                )}
                              </div>
                              <p className="text-sm text-gray-900 font-medium">
                                {policy.customerName}
                              </p>
                              <p className="text-sm text-gray-600">
                                {policy.productName} ‚Ä¢ {policy.coverageLevel}
                              </p>
                              <p className="text-sm text-gray-500">
                                Expires: {format(new Date(policy.expirationDate), 'MMM dd, yyyy')}
                              </p>
                            </div>
                            <div className="text-right">
                              <p className="text-sm font-medium">
                                ${policy.premiumAmount.toLocaleString()}/year
                              </p>
                              <p className="text-xs text-gray-500">
                                {policy.paymentFrequency}
                              </p>
                              {getStatusIcon(policy.status)}
                            </div>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Policy Details */}
          <div className="lg:col-span-1">
            {selectedPolicy ? (
              <Card>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg">Policy Details</CardTitle>
                    <div className="flex space-x-2">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => generateDocumentMutation.mutate({ 
                          policyId: selectedPolicy.id, 
                          documentType: 'certificate' 
                        })}
                        disabled={generateDocumentMutation.isPending}
                        data-testid="button-generate-certificate"
                      >
                        <FileCheck className="h-4 w-4 mr-2" />
                        Certificate
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => sendRenewalMutation.mutate(selectedPolicy.id)}
                        disabled={sendRenewalMutation.isPending}
                        data-testid="button-send-renewal"
                      >
                        <Send className="h-4 w-4 mr-2" />
                        Renewal
                      </Button>
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
                    <TabsList className="grid w-full grid-cols-4">
                      <TabsTrigger value="overview">Overview</TabsTrigger>
                      <TabsTrigger value="coverage">Coverage</TabsTrigger>
                      <TabsTrigger value="documents">Documents</TabsTrigger>
                      <TabsTrigger value="claims">Claims</TabsTrigger>
                    </TabsList>

                    <TabsContent value="overview" className="space-y-4">
                      <div>
                        <Label className="text-sm font-medium">Policy Number</Label>
                        <p className="text-sm text-gray-600" data-testid="text-policy-number">
                          {selectedPolicy.policyNumber}
                        </p>
                      </div>

                      <div>
                        <Label className="text-sm font-medium">Customer Information</Label>
                        <div className="space-y-1">
                          <p className="text-sm text-gray-600">
                            <UserCheck className="h-3 w-3 inline mr-1" />
                            {selectedPolicy.customerName}
                          </p>
                          <p className="text-sm text-gray-600">
                            <Mail className="h-3 w-3 inline mr-1" />
                            {selectedPolicy.customerEmail}
                          </p>
                          <p className="text-sm text-gray-600">
                            <Phone className="h-3 w-3 inline mr-1" />
                            {selectedPolicy.customerPhone}
                          </p>
                        </div>
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <Label className="text-sm font-medium">Effective Date</Label>
                          <p className="text-sm text-gray-600">
                            {format(new Date(selectedPolicy.effectiveDate), 'MMM dd, yyyy')}
                          </p>
                        </div>
                        <div>
                          <Label className="text-sm font-medium">Expiration Date</Label>
                          <p className="text-sm text-gray-600">
                            {format(new Date(selectedPolicy.expirationDate), 'MMM dd, yyyy')}
                          </p>
                        </div>
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <Label className="text-sm font-medium">Premium</Label>
                          <p className="text-lg font-semibold text-green-600">
                            ${selectedPolicy.premiumAmount.toLocaleString()}
                          </p>
                        </div>
                        <div>
                          <Label className="text-sm font-medium">Deductible</Label>
                          <p className="text-lg font-semibold text-blue-600">
                            ${selectedPolicy.deductible.toLocaleString()}
                          </p>
                        </div>
                      </div>

                      <div className="space-y-2">
                        <Label className="text-sm font-medium">Update Status</Label>
                        <Select
                          value={selectedPolicy.status}
                          onValueChange={(status) => 
                            updateStatusMutation.mutate({ 
                              policyId: selectedPolicy.id, 
                              status 
                            })
                          }
                        >
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="active">Active</SelectItem>
                            <SelectItem value="pending">Pending</SelectItem>
                            <SelectItem value="cancelled">Cancelled</SelectItem>
                            <SelectItem value="suspended">Suspended</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                    </TabsContent>

                    <TabsContent value="coverage" className="space-y-4">
                      <div>
                        <Label className="text-sm font-medium">Product Details</Label>
                        <div className="space-y-2">
                          <p className="text-sm text-gray-600">
                            <Shield className="h-3 w-3 inline mr-1" />
                            {selectedPolicy.productName}
                          </p>
                          <p className="text-sm text-gray-600">
                            Coverage Level: {selectedPolicy.coverageLevel}
                          </p>
                          <p className="text-sm text-gray-600">
                            Payment: {selectedPolicy.paymentFrequency} ‚Ä¢ {selectedPolicy.paymentMethod}
                          </p>
                        </div>
                      </div>

                      {selectedPolicy.vehicle && (
                        <div>
                          <Label className="text-sm font-medium">Vehicle Information</Label>
                          <div className="space-y-1">
                            <p className="text-sm text-gray-600">
                              <Car className="h-3 w-3 inline mr-1" />
                              {selectedPolicy.vehicle.year} {selectedPolicy.vehicle.make} {selectedPolicy.vehicle.model}
                            </p>
                            <p className="text-sm text-gray-600">
                              VIN: {selectedPolicy.vehicle.vin}
                            </p>
                            <p className="text-sm text-gray-600">
                              Mileage: {selectedPolicy.vehicle.mileage.toLocaleString()} miles
                            </p>
                          </div>
                        </div>
                      )}

                      {selectedPolicy.property && (
                        <div>
                          <Label className="text-sm font-medium">Property Information</Label>
                          <div className="space-y-1">
                            <p className="text-sm text-gray-600">
                              <Home className="h-3 w-3 inline mr-1" />
                              {selectedPolicy.property.propertyType}
                            </p>
                            <p className="text-sm text-gray-600">
                              <MapPin className="h-3 w-3 inline mr-1" />
                              {selectedPolicy.property.address}
                            </p>
                            <p className="text-sm text-gray-600">
                              {selectedPolicy.property.squareFootage.toLocaleString()} sq ft ‚Ä¢ Built {selectedPolicy.property.yearBuilt}
                            </p>
                          </div>
                        </div>
                      )}

                      <div>
                        <Label className="text-sm font-medium">Renewal Information</Label>
                        <div className="space-y-1">
                          <p className="text-sm text-gray-600">
                            Auto Renew: {selectedPolicy.renewalInfo.autoRenew ? 'Yes' : 'No'}
                          </p>
                          <p className="text-sm text-gray-600">
                            Renewal Date: {format(new Date(selectedPolicy.renewalInfo.renewalDate), 'MMM dd, yyyy')}
                          </p>
                          <p className="text-sm text-gray-600">
                            Renewal Premium: ${selectedPolicy.renewalInfo.renewalPremium.toLocaleString()}
                          </p>
                        </div>
                      </div>
                    </TabsContent>

                    <TabsContent value="documents" className="space-y-4">
                      <div>
                        <Label className="text-sm font-medium">Policy Documents</Label>
                        <div className="space-y-2 mt-2">
                          {selectedPolicy.documents.length === 0 ? (
                            <p className="text-sm text-gray-500">No documents available</p>
                          ) : (
                            selectedPolicy.documents.map((doc) => (
                              <div key={doc.id} className="flex items-center justify-between p-2 border rounded">
                                <div className="flex items-center space-x-2">
                                  <FileText className="h-4 w-4 text-gray-500" />
                                  <div>
                                    <p className="text-sm font-medium">{doc.name}</p>
                                    <p className="text-xs text-gray-500">
                                      {doc.type} ‚Ä¢ {format(new Date(doc.createdAt), 'MMM dd, yyyy')}
                                    </p>
                                  </div>
                                </div>
                                <Button size="sm" variant="outline">
                                  <Download className="h-3 w-3" />
                                </Button>
                              </div>
                            ))
                          )}
                        </div>
                      </div>

                      <div className="space-y-2">
                        <Label className="text-sm font-medium">Generate New Document</Label>
                        <div className="flex space-x-2">
                          <Button
                            size="sm"
                            onClick={() => generateDocumentMutation.mutate({ 
                              policyId: selectedPolicy.id, 
                              documentType: 'policy' 
                            })}
                            disabled={generateDocumentMutation.isPending}
                          >
                            Policy Document
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => generateDocumentMutation.mutate({ 
                              policyId: selectedPolicy.id, 
                              documentType: 'certificate' 
                            })}
                            disabled={generateDocumentMutation.isPending}
                          >
                            Certificate
                          </Button>
                        </div>
                      </div>
                    </TabsContent>

                    <TabsContent value="claims" className="space-y-4">
                      <div>
                        <Label className="text-sm font-medium">Associated Claims</Label>
                        <div className="space-y-2 mt-2">
                          {selectedPolicy.claims.length === 0 ? (
                            <p className="text-sm text-gray-500">No claims filed</p>
                          ) : (
                            selectedPolicy.claims.map((claim) => (
                              <div key={claim.id} className="p-2 border rounded">
                                <div className="flex items-center justify-between">
                                  <div>
                                    <p className="text-sm font-medium">{claim.claimNumber}</p>
                                    <p className="text-xs text-gray-500">
                                      {format(new Date(claim.dateOfLoss), 'MMM dd, yyyy')} ‚Ä¢ {claim.status}
                                    </p>
                                  </div>
                                  <p className="text-sm font-semibold">
                                    ${claim.amount.toLocaleString()}
                                  </p>
                                </div>
                              </div>
                            ))
                          )}
                        </div>
                      </div>
                    </TabsContent>
                  </Tabs>
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardContent className="text-center py-12">
                  <Shield className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-500">Select a policy to view details</p>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

