import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Server,
  Database,
  Zap,
  Activity,
  AlertTriangle,
  CheckCircle,
  Clock,
  XCircle,
  RefreshCw,
  Settings,
  MonitorSpeaker,
  CloudLightning,
  BarChart3,
  LineChart,
  TrendingUp,
  TrendingDown,
  Cpu,
  HardDrive,
  Wifi,
  Shield,
  Eye,
  Download,
  Upload,
  Play,
  Pause,
  Square,
  RotateCcw,
  Bell,
  FileText,
  Code,
  Globe,
  Lock,
  Unlock,
  Power,
  PowerOff
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { format } from "date-fns";

interface SystemStatus {
  id: string;
  name: string;
  type: "service" | "database" | "api" | "external";
  status: "online" | "offline" | "warning" | "maintenance";
  uptime: number;
  responseTime: number;
  lastCheck: string;
  healthScore: number;
  dependencies: string[];
  version: string;
  endpoint?: string;
}

interface Integration {
  id: string;
  name: string;
  type: "webhook" | "api" | "database" | "file_transfer" | "email";
  status: "active" | "inactive" | "error" | "pending";
  provider: string;
  lastSync: string;
  syncFrequency: string;
  recordsProcessed: number;
  errorCount: number;
  successRate: number;
  configuration: {
    [key: string]: any;
  };
}

interface WorkflowAutomation {
  id: string;
  name: string;
  type: "policy_issuance" | "claim_processing" | "renewal" | "notification" | "reporting";
  status: "running" | "paused" | "error" | "completed";
  trigger: string;
  actions: string[];
  lastRun: string;
  successCount: number;
  errorCount: number;
  avgExecutionTime: number;
  schedule?: string;
}

interface SystemMetric {
  name: string;
  value: number;
  unit: string;
  status: "good" | "warning" | "critical";
  trend: "up" | "down" | "stable";
  threshold: {
    warning: number;
    critical: number;
  };
}

export default function SystemIntegration() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedSystem, setSelectedSystem] = useState<SystemStatus | null>(null);
  const [selectedIntegration, setSelectedIntegration] = useState<Integration | null>(null);
  const [filterStatus, setFilterStatus] = useState("all");

  // Fetch system status
  const { data: systemStatuses = [], isLoading: systemLoading } = useQuery({
    queryKey: ["/api/system/status"],
    refetchInterval: 5000, // Real-time monitoring
  });

  // Fetch integrations
  const { data: integrations = [], isLoading: integrationLoading } = useQuery({
    queryKey: ["/api/system/integrations"],
    refetchInterval: 10000,
  });

  // Fetch workflow automations
  const { data: workflows = [] } = useQuery({
    queryKey: ["/api/system/workflows"],
    refetchInterval: 15000,
  });

  // Fetch system metrics
  const { data: metrics = [] } = useQuery({
    queryKey: ["/api/system/metrics"],
    refetchInterval: 30000,
  });

  // Restart system mutation
  const restartSystemMutation = useMutation({
    mutationFn: async (systemId: string) => {
      return await apiRequest("POST", `/api/system/${systemId}/restart`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/system/status"] });
      toast({
        title: "System Restarted",
        description: "System has been successfully restarted",
      });
    },
  });

  // Toggle integration mutation
  const toggleIntegrationMutation = useMutation({
    mutationFn: async ({ integrationId, action }: { integrationId: string; action: "enable" | "disable" | "sync" }) => {
      return await apiRequest("POST", `/api/system/integrations/${integrationId}/${action}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/system/integrations"] });
      toast({
        title: "Integration Updated",
        description: "Integration status has been updated successfully",
      });
    },
  });

  // Control workflow mutation
  const controlWorkflowMutation = useMutation({
    mutationFn: async ({ workflowId, action }: { workflowId: string; action: "start" | "pause" | "stop" | "reset" }) => {
      return await apiRequest("POST", `/api/system/workflows/${workflowId}/${action}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/system/workflows"] });
      toast({
        title: "Workflow Updated",
        description: "Workflow has been updated successfully",
      });
    },
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'online':
      case 'active':
      case 'running':
        return 'text-green-600 bg-green-100';
      case 'warning':
      case 'pending':
        return 'text-yellow-600 bg-yellow-100';
      case 'offline':
      case 'error':
        return 'text-red-600 bg-red-100';
      case 'maintenance':
      case 'paused':
        return 'text-blue-600 bg-blue-100';
      default:
        return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'online':
      case 'active':
      case 'running':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'warning':
      case 'pending':
        return <AlertTriangle className="h-4 w-4 text-yellow-500" />;
      case 'offline':
      case 'error':
        return <XCircle className="h-4 w-4 text-red-500" />;
      case 'maintenance':
      case 'paused':
        return <Clock className="h-4 w-4 text-blue-500" />;
      default:
        return <Activity className="h-4 w-4 text-gray-500" />;
    }
  };

  const getSystemIcon = (type: string) => {
    switch (type) {
      case 'service': return Server;
      case 'database': return Database;
      case 'api': return Zap;
      default: return Activity;
    }
  };

  const getIntegrationIcon = (type: string) => {
    switch (type) {
      case 'webhook': return CloudLightning;
      case 'api': return Globe;
      case 'database': return Database;
      case 'file_transfer': return Upload;
      case 'email': return Bell;
      default: return Zap;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">System Integration Hub</h1>
            <p className="text-gray-600 mt-2">Real-time monitoring, integrations, and workflow automation</p>
          </div>
          <div className="flex space-x-4">
            <Button variant="outline" data-testid="button-export-logs">
              <Download className="h-4 w-4 mr-2" />
              Export Logs
            </Button>
            <Button data-testid="button-system-settings">
              <Settings className="h-4 w-4 mr-2" />
              Settings
            </Button>
          </div>
        </div>

        {/* System Health Overview */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">System Health</CardTitle>
              <Activity className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600" data-testid="text-system-health">
                98.5%
              </div>
              <p className="text-xs text-muted-foreground">
                All systems operational
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Active Integrations</CardTitle>
              <Zap className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-active-integrations">
                {integrations.filter((i: Integration) => i.status === 'active').length}
              </div>
              <p className="text-xs text-muted-foreground">
                of {integrations.length} total
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Workflows Running</CardTitle>
              <RefreshCw className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-running-workflows">
                {workflows.filter((w: WorkflowAutomation) => w.status === 'running').length}
              </div>
              <p className="text-xs text-muted-foreground">
                Automated processes
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Avg Response Time</CardTitle>
              <Clock className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-response-time">
                145ms
              </div>
              <p className="text-xs text-muted-foreground">
                System performance
              </p>
            </CardContent>
          </Card>
        </div>

        {/* Main Content Tabs */}
        <Tabs defaultValue="systems" className="space-y-6">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="systems">System Status</TabsTrigger>
            <TabsTrigger value="integrations">Integrations</TabsTrigger>
            <TabsTrigger value="workflows">Workflows</TabsTrigger>
            <TabsTrigger value="metrics">Performance</TabsTrigger>
          </TabsList>

          {/* System Status Tab */}
          <TabsContent value="systems" className="space-y-6">
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              <div className="lg:col-span-2">
                <Card>
                  <CardHeader>
                    <CardTitle>System Components</CardTitle>
                    <CardDescription>Real-time status of all system components</CardDescription>
                  </CardHeader>
                  <CardContent>
                    {systemLoading ? (
                      <div className="text-center py-8">
                        <RefreshCw className="h-8 w-8 animate-spin mx-auto mb-2" />
                        <p>Loading system status...</p>
                      </div>
                    ) : (
                      <div className="space-y-4">
                        {systemStatuses.map((system: SystemStatus) => {
                          const SystemIcon = getSystemIcon(system.type);

                          return (
                            <div
                              key={system.id}
                              className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50 cursor-pointer"
                              onClick={() => setSelectedSystem(system)}
                              data-testid={`system-item-${system.id}`}
                            >
                              <div className="flex items-center space-x-3">
                                <SystemIcon className="h-5 w-5 text-gray-500" />
                                <div>
                                  <p className="font-medium">{system.name}</p>
                                  <p className="text-sm text-gray-600">
                                    {system.type} ‚Ä¢ v{system.version}
                                  </p>
                                </div>
                              </div>
                              <div className="flex items-center space-x-4">
                                <div className="text-right">
                                  <p className="text-sm font-medium">{system.responseTime}ms</p>
                                  <p className="text-xs text-gray-500">
                                    {system.uptime.toFixed(1)}% uptime
                                  </p>
                                </div>
                                <Badge className={getStatusColor(system.status)}>
                                  {getStatusIcon(system.status)}
                                  <span className="ml-1">{system.status}</span>
                                </Badge>
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    )}
                  </CardContent>
                </Card>
              </div>

              <div>
                {selectedSystem ? (
                  <Card>
                    <CardHeader>
                      <CardTitle>System Details</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <div>
                        <Label className="text-sm font-medium">System Name</Label>
                        <p className="text-sm text-gray-600">{selectedSystem.name}</p>
                      </div>

                      <div>
                        <Label className="text-sm font-medium">Health Score</Label>
                        <div className="flex items-center space-x-2 mt-1">
                          <div className="flex-1 bg-gray-200 rounded-full h-2">
                            <div 
                              className={`h-2 rounded-full ${
                                selectedSystem.healthScore >= 90 ? 'bg-green-500' :
                                selectedSystem.healthScore >= 70 ? 'bg-yellow-500' : 'bg-red-500'
                              }`}
                              style={{ width: `${selectedSystem.healthScore}%` }}
                            />
                          </div>
                          <span className="text-sm font-medium">{selectedSystem.healthScore}%</span>
                        </div>
                      </div>

                      <div>
                        <Label className="text-sm font-medium">Dependencies</Label>
                        <div className="space-y-1 mt-1">
                          {selectedSystem.dependencies.map((dep, index) => (
                            <Badge key={index} variant="outline" className="mr-1">
                              {dep}
                            </Badge>
                          ))}
                        </div>
                      </div>

                      <div>
                        <Label className="text-sm font-medium">Last Check</Label>
                        <p className="text-sm text-gray-600">
                          {format(new Date(selectedSystem.lastCheck), 'MMM dd, yyyy HH:mm:ss')}
                        </p>
                      </div>

                      <div className="flex space-x-2">
                        <Button
                          size="sm"
                          onClick={() => restartSystemMutation.mutate(selectedSystem.id)}
                          disabled={restartSystemMutation.isPending}
                          data-testid="button-restart-system"
                        >
                          <RotateCcw className="h-4 w-4 mr-2" />
                          Restart
                        </Button>
                        <Button size="sm" variant="outline">
                          <Eye className="h-4 w-4 mr-2" />
                          View Logs
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ) : (
                  <Card>
                    <CardContent className="text-center py-12">
                      <Server className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                      <p className="text-gray-500">Select a system to view details</p>
                    </CardContent>
                  </Card>
                )}
              </div>
            </div>
          </TabsContent>

          {/* Integrations Tab */}
          <TabsContent value="integrations" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Active Integrations</CardTitle>
                <CardDescription>External system integrations and data synchronization</CardDescription>
              </CardHeader>
              <CardContent>
                {integrationLoading ? (
                  <div className="text-center py-8">
                    <RefreshCw className="h-8 w-8 animate-spin mx-auto mb-2" />
                    <p>Loading integrations...</p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {integrations.map((integration: Integration) => {
                      const IntegrationIcon = getIntegrationIcon(integration.type);

                      return (
                        <div
                          key={integration.id}
                          className="flex items-center justify-between p-4 border rounded-lg"
                          data-testid={`integration-item-${integration.id}`}
                        >
                          <div className="flex items-center space-x-3">
                            <IntegrationIcon className="h-5 w-5 text-gray-500" />
                            <div>
                              <p className="font-medium">{integration.name}</p>
                              <p className="text-sm text-gray-600">
                                {integration.provider} ‚Ä¢ {integration.syncFrequency}
                              </p>
                            </div>
                          </div>

                          <div className="flex items-center space-x-4">
                            <div className="text-right">
                              <p className="text-sm font-medium">
                                {integration.recordsProcessed.toLocaleString()} records
                              </p>
                              <p className="text-xs text-gray-500">
                                {integration.successRate.toFixed(1)}% success
                              </p>
                            </div>

                            <div className="flex items-center space-x-2">
                              <Badge className={getStatusColor(integration.status)}>
                                {getStatusIcon(integration.status)}
                                <span className="ml-1">{integration.status}</span>
                              </Badge>

                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => toggleIntegrationMutation.mutate({
                                  integrationId: integration.id,
                                  action: integration.status === 'active' ? 'disable' : 'enable'
                                })}
                                data-testid={`button-toggle-${integration.id}`}
                              >
                                {integration.status === 'active' ? <Pause /> : <Play />}
                              </Button>

                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => toggleIntegrationMutation.mutate({
                                  integrationId: integration.id,
                                  action: 'sync'
                                })}
                                data-testid={`button-sync-${integration.id}`}
                              >
                                <RefreshCw className="h-4 w-4" />
                              </Button>
                            </div>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* Workflows Tab */}
          <TabsContent value="workflows" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Workflow Automations</CardTitle>
                <CardDescription>Automated business processes and triggers</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {workflows.map((workflow: WorkflowAutomation) => (
                    <div
                      key={workflow.id}
                      className="flex items-center justify-between p-4 border rounded-lg"
                      data-testid={`workflow-item-${workflow.id}`}
                    >
                      <div className="flex items-center space-x-3">
                        <RefreshCw className="h-5 w-5 text-gray-500" />
                        <div>
                          <p className="font-medium">{workflow.name}</p>
                          <p className="text-sm text-gray-600">
                            {workflow.type} ‚Ä¢ Trigger: {workflow.trigger}
                          </p>
                        </div>
                      </div>

                      <div className="flex items-center space-x-4">
                        <div className="text-right">
                          <p className="text-sm font-medium">
                            {workflow.successCount} successful
                          </p>
                          <p className="text-xs text-gray-500">
                            Avg: {workflow.avgExecutionTime}s
                          </p>
                        </div>

                        <div className="flex items-center space-x-2">
                          <Badge className={getStatusColor(workflow.status)}>
                            {getStatusIcon(workflow.status)}
                            <span className="ml-1">{workflow.status}</span>
                          </Badge>

                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => controlWorkflowMutation.mutate({
                              workflowId: workflow.id,
                              action: workflow.status === 'running' ? 'pause' : 'start'
                            })}
                            data-testid={`button-control-${workflow.id}`}
                          >
                            {workflow.status === 'running' ? <Pause /> : <Play />}
                          </Button>

                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => controlWorkflowMutation.mutate({
                              workflowId: workflow.id,
                              action: 'reset'
                            })}
                          >
                            <RotateCcw className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Performance Metrics Tab */}
          <TabsContent value="metrics" className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {metrics.map((metric: SystemMetric, index: number) => (
                <Card key={index}>
                  <CardHeader>
                    <CardTitle className="flex items-center justify-between">
                      <span>{metric.name}</span>
                      <div className="flex items-center space-x-1">
                        {metric.trend === 'up' ? (
                          <TrendingUp className="h-4 w-4 text-green-500" />
                        ) : metric.trend === 'down' ? (
                          <TrendingDown className="h-4 w-4 text-red-500" />
                        ) : (
                          <Activity className="h-4 w-4 text-gray-500" />
                        )}
                        <Badge className={
                          metric.status === 'good' ? 'bg-green-100 text-green-800' :
                          metric.status === 'warning' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }>
                          {metric.status}
                        </Badge>
                      </div>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="text-3xl font-bold mb-2" data-testid={`metric-${metric.name.toLowerCase().replace(/\s+/g, '-')}`}>
                      {metric.value}{metric.unit}
                    </div>
                    <div className="space-y-2">
                      <div className="flex justify-between text-sm">
                        <span>Warning</span>
                        <span>{metric.threshold.warning}{metric.unit}</span>
                      </div>
                      <div className="flex justify-between text-sm">
                        <span>Critical</span>
                        <span>{metric.threshold.critical}{metric.unit}</span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                        <div 
                          className={`h-2 rounded-full ${
                            metric.status === 'good' ? 'bg-green-500' :
                            metric.status === 'warning' ? 'bg-yellow-500' : 'bg-red-500'
                          }`}
                          style={{ 
                            width: `${Math.min((metric.value / metric.threshold.critical) * 100, 100)}%` 
                          }}
                        />
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/VSCQuoteResults.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { CheckCircle, Shield, Star, Award, Share2, Download, ArrowLeft } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";

interface VehicleInfo {
  vin: string;
  year: string;
  make: string;
  model: string;
  mileage: number;
  vehicleClass: string;
  source?: string;
  error?: string;
}

interface CustomerInfo {
  name: string;
  email: string;
  zipcode: string;
}

interface CoverageLevel {
  name: string;
  tier: "Platinum" | "Gold" | "Silver";
  price: number;
  description: string;
  features: string[];
  deductible: number;
  termMonths: number;
  coverageMiles: number;
  icon: any;
  popular?: boolean;
}

export default function VSCQuoteResults() {
  const [, setLocation] = useLocation();
  const [vehicleInfo, setVehicleInfo] = useState<VehicleInfo | null>(null);
  const [customerInfo, setCustomerInfo] = useState<CustomerInfo | null>(null);
  const [coverageLevels, setCoverageLevels] = useState<CoverageLevel[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadQuoteData = async () => {
      // Debug logging
      console.log("üîç VSCQuoteResults: Checking for quote data...");

      // Get quote data from sessionStorage (matching NewLanding.tsx)
      const savedQuote = sessionStorage.getItem('vscQuoteData');
      console.log("üîç VSCQuoteResults: sessionStorage data:", savedQuote);

      if (savedQuote) {
        console.log("‚úÖ VSCQuoteResults: Found quote data, processing...");
        const formData = JSON.parse(savedQuote);

        // Decode VIN to get vehicle information
        let vehicleInfo = null;

        try {
          console.log("üîç Calling VIN decode API for:", formData.vin);
          const response = await fetch(`/api/vin-decode/${formData.vin}`);

          if (response.ok) {
            const vinData = await response.json();
            console.log("üìã VIN decode response:", vinData);

            vehicleInfo = {
              vin: formData.vin,
              year: vinData.year?.toString() || "Unknown",
              make: vinData.make === "Unknown" ? "Unknown" : vinData.make,
              model: vinData.model === "Unknown" ? "Unknown" : vinData.model,
              mileage: formData.mileage,
              vehicleClass: "Class A",
              source: vinData.source || "Unknown"
            };

            console.log("‚úÖ Processed vehicle info:", vehicleInfo);
          } else {
            console.error("‚ùå VIN decode API failed:", response.status, response.statusText);
            throw new Error(`VIN decode failed: ${response.statusText}`);
          }
        } catch (error) {
          console.error("‚ùå VIN decode error:", error);

          // Show error state instead of fallback data
          vehicleInfo = {
            vin: formData.vin,
            year: "Unknown",
            make: "Unknown", 
            model: "Unknown",
            mileage: formData.mileage,
            vehicleClass: "Unknown",
            error: "VIN could not be decoded or is invalid"
          };
        }

        // Create customer info structure  
        const customerInfo = {
          name: formData.fullName,
          email: formData.email,
          zipcode: formData.zipCode
        };

        setVehicleInfo(vehicleInfo);
        setCustomerInfo(customerInfo);

        // Create VSC coverage levels with real pricing
        const levels: CoverageLevel[] = [
        {
          name: "Elevate Platinum",
          tier: "Platinum",
          price: 2349.99,
          description: "Maximum protection with comprehensive coverage for all major components",
          features: [
            "Engine & Transmission Coverage",
            "Electrical System Protection", 
            "A/C & Heating Coverage",
            "Fuel System Protection",
            "Brake System Coverage",
            "Steering & Suspension",
            "24/7 Roadside Assistance",
            "$100 Trip Interruption",
            "Rental Car Reimbursement",
            "Towing Coverage"
          ],
          deductible: 100,
          termMonths: 60,
          coverageMiles: 100000,
          icon: Award,
          popular: true
        },
        {
          name: "Elevate Gold", 
          tier: "Gold",
          price: 1894.46,
          description: "Comprehensive protection for essential vehicle systems",
          features: [
            "Engine & Transmission Coverage",
            "Electrical System Protection",
            "A/C & Heating Coverage", 
            "Fuel System Protection",
            "Brake System Coverage",
            "24/7 Roadside Assistance",
            "Towing Coverage"
          ],
          deductible: 200,
          termMonths: 48,
          coverageMiles: 75000,
          icon: Star
        },
        {
          name: "Pinnacle Silver",
          tier: "Silver", 
          price: 1299.99,
          description: "Essential coverage for major powertrain components",
          features: [
            "Engine Coverage",
            "Transmission Coverage",
            "Basic Electrical Protection",
            "24/7 Roadside Assistance",
            "Towing Coverage"
          ],
          deductible: 300,
          termMonths: 36,
          coverageMiles: 50000,
          icon: Shield
        }
        ];

        setCoverageLevels(levels);
        console.log("‚úÖ VSCQuoteResults: Successfully loaded coverage levels");
      } else {
        console.log("‚ùå VSCQuoteResults: No quote data found in sessionStorage");
      }

      setLoading(false);
    };

    loadQuoteData();
  }, []);

  const handleSelectCoverage = (coverage: CoverageLevel) => {
    const purchaseData = {
      vehicle: vehicleInfo,
      customer: customerInfo,
      coverage: coverage,
      timestamp: new Date().toISOString()
    };

    localStorage.setItem('selectedCoverage', JSON.stringify(purchaseData));
    setLocation('/purchase');
  };

  const handleShareQuote = () => {
    // Share functionality
    const shareText = `VSC Quote for ${vehicleInfo?.year} ${vehicleInfo?.make} ${vehicleInfo?.model}`;
    if (navigator.share) {
      navigator.share({
        title: shareText,
        text: 'Check out my vehicle service contract quote',
        url: window.location.href
      });
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Calculating your quotes...</p>
        </div>
      </div>
    );
  }

  if (!vehicleInfo) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-6">
          <Alert className="mb-4">
            <AlertDescription>
              <div className="space-y-2">
                <p className="font-semibold">No quote data found</p>
                <p className="text-sm text-gray-600">
                  To get a VSC quote, please fill out the form on our homepage with your vehicle information.
                </p>
              </div>
            </AlertDescription>
          </Alert>
          <Button onClick={() => {
            console.log("Redirecting to homepage for new quote");
            window.location.href = '/';
          }} className="mt-4 bg-blue-600 hover:bg-blue-700">
            Start New VSC Quote
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <Button variant="outline" onClick={() => setLocation('/')}>
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Quote
          </Button>
          <div className="flex gap-2">
            <Button variant="outline" onClick={handleShareQuote}>
              <Share2 className="h-4 w-4 mr-2" />
              Share Quote
            </Button>
          </div>
        </div>

        {/* Vehicle Information */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Shield className="h-5 w-5 text-blue-600" />
              Vehicle Service Contract Quotes
            </CardTitle>
          </CardHeader>
          <CardContent>
            {vehicleInfo.error && (
              <Alert className="mb-4 border-yellow-200 bg-yellow-50">
                <AlertDescription className="text-yellow-800">
                  <div className="flex items-center gap-2">
                    <span className="text-yellow-600">‚ö†Ô∏è</span>
                    <div>
                      <p className="font-medium">VIN Validation Notice</p>
                      <p className="text-sm">The VIN provided appears to be invalid or could not be decoded. Vehicle information may be incomplete. Please verify your VIN and try again if needed.</p>
                    </div>
                  </div>
                </AlertDescription>
              </Alert>
            )}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
              <div>
                <span className="font-medium text-gray-600">Vehicle:</span>
                <p className="font-semibold">
                  {vehicleInfo.year} {vehicleInfo.make} {vehicleInfo.model}
                  {vehicleInfo.make === "Unknown" && <span className="text-gray-500 ml-1">(VIN Decode Failed)</span>}
                </p>
              </div>
              <div>
                <span className="font-medium text-gray-600">VIN:</span>
                <p className="font-mono text-xs">{vehicleInfo.vin}</p>
              </div>
              <div>
                <span className="font-medium text-gray-600">Current Mileage:</span>
                <p className="font-semibold">{vehicleInfo.mileage.toLocaleString()} miles</p>
              </div>
              <div>
                <span className="font-medium text-gray-600">Vehicle Class:</span>
                <p className="font-semibold">{vehicleInfo.vehicleClass}</p>
              </div>
            </div>
            {vehicleInfo.source && (
              <div className="mt-3 text-xs text-gray-500">
                Data source: {vehicleInfo.source}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Coverage Options */}
        <div className="grid md:grid-cols-3 gap-6">
          {coverageLevels.map((coverage) => {
            const IconComponent = coverage.icon;
            return (
              <Card key={coverage.name} className={`relative ${coverage.popular ? 'border-blue-500 border-2' : ''}`}>
                {coverage.popular && (
                  <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                    <Badge className="bg-blue-600 text-white px-3 py-1">Most Popular</Badge>
                  </div>
                )}

                <CardHeader className="text-center pb-4">
                  <div className="flex justify-center mb-2">
                    <IconComponent className="h-12 w-12 text-blue-600" />
                  </div>
                  <CardTitle className="text-xl">{coverage.name}</CardTitle>
                  <div className="text-3xl font-bold text-blue-600">
                    ${coverage.price.toLocaleString()}
                  </div>
                  <p className="text-sm text-gray-600">{coverage.description}</p>
                </CardHeader>

                <CardContent className="space-y-4">
                  {/* Coverage Details */}
                  <div className="grid grid-cols-2 gap-2 text-sm bg-gray-50 p-3 rounded-lg">
                    <div>
                      <span className="font-medium">Term:</span> {coverage.termMonths} months
                    </div>
                    <div>
                      <span className="font-medium">Miles:</span> {coverage.coverageMiles.toLocaleString()}
                    </div>
                    <div>
                      <span className="font-medium">Deductible:</span> ${coverage.deductible}
                    </div>
                    <div>
                      <span className="font-medium">Tier:</span> {coverage.tier}
                    </div>
                  </div>

                  {/* Features */}
                  <div className="space-y-2">
                    <h4 className="font-semibold text-sm">What's Covered:</h4>
                    <div className="space-y-1 max-h-48 overflow-y-auto">
                      {coverage.features.map((feature, index) => (
                        <div key={index} className="flex items-start gap-2 text-sm">
                          <CheckCircle className="h-4 w-4 text-green-600 mt-0.5 flex-shrink-0" />
                          <span>{feature}</span>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Select Button */}
                  <Button 
                    onClick={() => handleSelectCoverage(coverage)}
                    className={`w-full ${coverage.popular ? 'bg-blue-600 hover:bg-blue-700' : ''}`}
                    size="lg"
                    data-testid={`button-select-${coverage.tier.toLowerCase()}`}
                  >
                    Select {coverage.tier} Coverage
                  </Button>
                </CardContent>
              </Card>
            );
          })}
        </div>

        {/* Additional Information */}
        <Card className="mt-8">
          <CardContent className="pt-6">
            <div className="grid md:grid-cols-2 gap-6">
              <div>
                <h3 className="font-semibold mb-3 flex items-center gap-2">
                  <Shield className="h-5 w-5 text-blue-600" />
                  Why Choose Vehicle Service Contract?
                </h3>
                <ul className="space-y-2 text-sm">
                  <li className="flex items-start gap-2">
                    <CheckCircle className="h-4 w-4 text-green-600 mt-0.5" />
                    <span>Protection against unexpected repair costs</span>
                  </li>
                  <li className="flex items-start gap-2">
                    <CheckCircle className="h-4 w-4 text-green-600 mt-0.5" />
                    <span>Nation-wide network of certified repair facilities</span>
                  </li>
                  <li className="flex items-start gap-2">
                    <CheckCircle className="h-4 w-4 text-green-600 mt-0.5" />
                    <span>24/7 roadside assistance included</span>
                  </li>
                  <li className="flex items-start gap-2">
                    <CheckCircle className="h-4 w-4 text-green-600 mt-0.5" />
                    <span>Transferable to new owner (adds resale value)</span>
                  </li>
                </ul>
              </div>

              <div>
                <h3 className="font-semibold mb-3">Important Notes:</h3>
                <ul className="space-y-2 text-sm text-gray-600">
                  <li>‚Ä¢ Prices shown are for full payment</li>
                  <li>‚Ä¢ Coverage begins 30 days after purchase or 1,000 miles</li>
                  <li>‚Ä¢ All plans include 24/7 customer service</li>
                  <li>‚Ä¢ 30-day money-back guarantee</li>
                  <li>‚Ä¢ Subject to terms and conditions</li>
                </ul>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/WhitelabelConfig.tsx.content:
import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Switch } from '@/components/ui/switch';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

export default function WhitelabelConfig() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [resellerId] = useState('reseller-001'); // In production, get from auth context

  // Branding state
  const [logoUrl, setLogoUrl] = useState('');
  const [primaryColor, setPrimaryColor] = useState('#2563eb');
  const [secondaryColor, setSecondaryColor] = useState('#1e40af');
  const [companyName, setCompanyName] = useState('');
  const [tagline, setTagline] = useState('');
  const [contactPhone, setContactPhone] = useState('');
  const [contactEmail, setContactEmail] = useState('');

  // Domain state
  const [subdomain, setSubdomain] = useState('');
  const [customDomain, setCustomDomain] = useState('');
  const [sslEnabled, setSslEnabled] = useState(true);

  // Product configuration
  const [productConfig, setProductConfig] = useState({
    autoAdvantage: { enabled: true, markup: 15, commission: 12 },
    homeProtection: { enabled: true, markup: 20, commission: 15 },
    allVehicle: { enabled: true, markup: 18, commission: 10 },
  });

  // Page content
  const [heroText, setHeroText] = useState('');
  const [ctaText, setCtaText] = useState('');
  const [companyStory, setCompanyStory] = useState('');

  const { data: config, isLoading } = useQuery({
    queryKey: [`/api/wholesale/white-label/config/${resellerId}`],
  });

  const updateConfigMutation = useMutation({
    mutationFn: async (configData: any) => {
      return apiRequest(`/api/wholesale/white-label/config/${resellerId}`, {
        method: 'PUT',
        body: JSON.stringify(configData),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/wholesale/white-label/config/${resellerId}`] });
      toast({
        title: "Configuration Updated",
        description: "Your white-label configuration has been saved successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Update Failed",
        description: "Failed to update white-label configuration.",
        variant: "destructive",
      });
    },
  });

  const setupDomainMutation = useMutation({
    mutationFn: async (domainData: any) => {
      return apiRequest('/api/wholesale/white-label/domain', {
        method: 'POST',
        body: JSON.stringify(domainData),
      });
    },
    onSuccess: (data) => {
      toast({
        title: "Domain Configuration Started",
        description: `Domain setup in progress. Estimated completion: ${data.estimatedCompletion}`,
      });
    },
  });

  // Load existing configuration
  useEffect(() => {
    if (config) {
      // Branding
      setLogoUrl(config.branding?.logoUrl || '');
      setPrimaryColor(config.branding?.primaryColor || '#2563eb');
      setSecondaryColor(config.branding?.secondaryColor || '#1e40af');
      setCompanyName(config.branding?.companyName || '');
      setTagline(config.branding?.tagline || '');
      setContactPhone(config.branding?.contactPhone || '');
      setContactEmail(config.branding?.contactEmail || '');

      // Domain
      setSubdomain(config.domain?.subdomain || '');
      setCustomDomain(config.domain?.customDomain || '');
      setSslEnabled(config.domain?.sslEnabled !== false);

      // Products
      if (config.products) {
        setProductConfig(config.products);
      }

      // Pages
      setHeroText(config.pages?.landingPage?.heroText || '');
      setCtaText(config.pages?.landingPage?.ctaText || '');
      setCompanyStory(config.pages?.aboutPage?.companyStory || '');
    }
  }, [config]);

  const handleSaveConfiguration = () => {
    const configData = {
      branding: {
        logoUrl,
        primaryColor,
        secondaryColor,
        companyName,
        tagline,
        contactPhone,
        contactEmail,
      },
      domain: {
        subdomain,
        customDomain,
        sslEnabled,
      },
      products: productConfig,
      pages: {
        landingPage: { heroText, ctaText },
        aboutPage: { companyStory },
      },
    };

    updateConfigMutation.mutate(configData);
  };

  const handleDomainSetup = () => {
    setupDomainMutation.mutate({
      resellerId,
      subdomain,
      customDomain,
      sslRequired: sslEnabled,
    });
  };

  const handleProductToggle = (productKey: string, enabled: boolean) => {
    setProductConfig(prev => ({
      ...prev,
      [productKey]: { ...prev[productKey], enabled }
    }));
  };

  const handleProductMarkup = (productKey: string, markup: number) => {
    setProductConfig(prev => ({
      ...prev,
      [productKey]: { ...prev[productKey], markup }
    }));
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-gray-200 rounded w-1/3"></div>
          <div className="h-4 bg-gray-200 rounded w-2/3"></div>
          <div className="space-y-3">
            <div className="h-4 bg-gray-200 rounded"></div>
            <div className="h-4 bg-gray-200 rounded w-5/6"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6" data-testid="whitelabel-config">
      <div>
        <h1 className="text-3xl font-bold tracking-tight">White-label Configuration</h1>
        <p className="text-muted-foreground mt-2">
          Customize your branded portal, subdomain, and embedded widgets for your customers.
        </p>
      </div>

      <Tabs defaultValue="branding" className="space-y-6">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="branding">Branding</TabsTrigger>
          <TabsTrigger value="domain">Domain Setup</TabsTrigger>
          <TabsTrigger value="products">Products</TabsTrigger>
          <TabsTrigger value="widgets">Widgets</TabsTrigger>
        </TabsList>

        <TabsContent value="branding" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Brand Identity</CardTitle>
              <CardDescription>
                Configure your company branding and visual identity
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="company-name">Company Name</Label>
                  <Input
                    id="company-name"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                    placeholder="Your Company Name"
                    data-testid="input-company-name"
                  />
                </div>
                <div>
                  <Label htmlFor="tagline">Tagline</Label>
                  <Input
                    id="tagline"
                    value={tagline}
                    onChange={(e) => setTagline(e.target.value)}
                    placeholder="Your company tagline"
                    data-testid="input-tagline"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="logo-url">Logo URL</Label>
                <Input
                  id="logo-url"
                  value={logoUrl}
                  onChange={(e) => setLogoUrl(e.target.value)}
                  placeholder="https://your-domain.com/logo.png"
                  data-testid="input-logo-url"
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="primary-color">Primary Color</Label>
                  <div className="flex gap-2">
                    <Input
                      id="primary-color"
                      type="color"
                      value={primaryColor}
                      onChange={(e) => setPrimaryColor(e.target.value)}
                      className="w-20"
                      data-testid="input-primary-color"
                    />
                    <Input
                      value={primaryColor}
                      onChange={(e) => setPrimaryColor(e.target.value)}
                      placeholder="#2563eb"
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="secondary-color">Secondary Color</Label>
                  <div className="flex gap-2">
                    <Input
                      id="secondary-color"
                      type="color"
                      value={secondaryColor}
                      onChange={(e) => setSecondaryColor(e.target.value)}
                      className="w-20"
                      data-testid="input-secondary-color"
                    />
                    <Input
                      value={secondaryColor}
                      onChange={(e) => setSecondaryColor(e.target.value)}
                      placeholder="#1e40af"
                    />
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="contact-phone">Contact Phone</Label>
                  <Input
                    id="contact-phone"
                    value={contactPhone}
                    onChange={(e) => setContactPhone(e.target.value)}
                    placeholder="1-800-YOUR-COMPANY"
                    data-testid="input-contact-phone"
                  />
                </div>
                <div>
                  <Label htmlFor="contact-email">Contact Email</Label>
                  <Input
                    id="contact-email"
                    type="email"
                    value={contactEmail}
                    onChange={(e) => setContactEmail(e.target.value)}
                    placeholder="info@yourcompany.com"
                    data-testid="input-contact-email"
                  />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Page Content</CardTitle>
              <CardDescription>
                Customize the content on your branded pages
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="hero-text">Landing Page Hero Text</Label>
                <Textarea
                  id="hero-text"
                  value={heroText}
                  onChange={(e) => setHeroText(e.target.value)}
                  placeholder="Protect your most valuable assets with our trusted coverage solutions"
                  data-testid="textarea-hero-text"
                />
              </div>

              <div>
                <Label htmlFor="cta-text">Call-to-Action Button Text</Label>
                <Input
                  id="cta-text"
                  value={ctaText}
                  onChange={(e) => setCtaText(e.target.value)}
                  placeholder="Get Your Free Quote Today"
                  data-testid="input-cta-text"
                />
              </div>

              <div>
                <Label htmlFor="company-story">Company Story</Label>
                <Textarea
                  id="company-story"
                  value={companyStory}
                  onChange={(e) => setCompanyStory(e.target.value)}
                  placeholder="Tell your customers about your company's mission and values"
                  rows={4}
                  data-testid="textarea-company-story"
                />
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="domain" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Domain Configuration</CardTitle>
              <CardDescription>
                Set up your custom subdomain or connect your own domain
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div>
                <Label htmlFor="subdomain">Subdomain</Label>
                <div className="flex items-center gap-2">
                  <Input
                    id="subdomain"
                    value={subdomain}
                    onChange={(e) => setSubdomain(e.target.value)}
                    placeholder="yourcompany"
                    className="flex-1"
                    data-testid="input-subdomain"
                  />
                  <span className="text-sm text-muted-foreground">.tpaplatform.com</span>
                </div>
                <p className="text-sm text-muted-foreground mt-1">
                  Your customers will access your portal at: <strong>{subdomain || 'yourcompany'}.tpaplatform.com</strong>
                </p>
              </div>

              <Separator />

              <div>
                <Label htmlFor="custom-domain">Custom Domain (Optional)</Label>
                <Input
                  id="custom-domain"
                  value={customDomain}
                  onChange={(e) => setCustomDomain(e.target.value)}
                  placeholder="insurance.yourcompany.com"
                  data-testid="input-custom-domain"
                />
                <p className="text-sm text-muted-foreground mt-1">
                  Use your own domain for a fully branded experience. You'll need to configure DNS settings.
                </p>
              </div>

              <div className="flex items-center space-x-2">
                <Switch
                  id="ssl-enabled"
                  checked={sslEnabled}
                  onCheckedChange={setSslEnabled}
                  data-testid="switch-ssl-enabled"
                />
                <Label htmlFor="ssl-enabled">Enable SSL Certificate</Label>
              </div>

              <div className="flex gap-2">
                <Button
                  onClick={handleDomainSetup}
                  disabled={setupDomainMutation.isPending}
                  data-testid="button-setup-domain"
                >
                  {setupDomainMutation.isPending ? 'Setting up...' : 'Configure Domain'}
                </Button>
              </div>

              {config?.domain?.domainStatus && (
                <div className="p-4 bg-muted rounded-lg">
                  <div className="flex items-center justify-between">
                    <span className="font-medium">Domain Status</span>
                    <Badge variant={config.domain.domainStatus === 'active' ? 'default' : 'secondary'}>
                      {config.domain.domainStatus}
                    </Badge>
                  </div>
                  {config.domain.dnsCname && (
                    <p className="text-sm text-muted-foreground mt-2">
                      DNS CNAME: {config.domain.dnsCname}
                    </p>
                  )}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="products" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Product Configuration</CardTitle>
              <CardDescription>
                Choose which products to offer and set your markup rates
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-6">
                {Object.entries(productConfig).map(([key, product]) => (
                  <div key={key} className="flex items-center justify-between p-4 border rounded-lg">
                    <div className="flex items-center space-x-4">
                      <Switch
                        checked={product.enabled}
                        onCheckedChange={(enabled) => handleProductToggle(key, enabled)}
                        data-testid={`switch-product-${key}`}
                      />
                      <div>
                        <h4 className="font-medium">
                          {key === 'autoAdvantage' && 'Auto Advantage Program'}
                          {key === 'homeProtection' && 'Home Protection Plan'}
                          {key === 'allVehicle' && 'All-Vehicle Protection'}
                        </h4>
                        <p className="text-sm text-muted-foreground">
                          Commission: {product.commission}%
                        </p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Label htmlFor={`markup-${key}`} className="text-sm">Markup:</Label>
                      <Input
                        id={`markup-${key}`}
                        type="number"
                        value={product.markup}
                        onChange={(e) => handleProductMarkup(key, parseInt(e.target.value) || 0)}
                        className="w-20"
                        min="0"
                        max="100"
                        disabled={!product.enabled}
                        data-testid={`input-markup-${key}`}
                      />
                      <span className="text-sm">%</span>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="widgets" className="space-y-6">
          <WidgetConfigurationSection resellerId={resellerId} />
        </TabsContent>
      </Tabs>

      <div className="flex justify-end">
        <Button
          onClick={handleSaveConfiguration}
          disabled={updateConfigMutation.isPending}
          size="lg"
          data-testid="button-save-config"
        >
          {updateConfigMutation.isPending ? 'Saving...' : 'Save Configuration'}
        </Button>
      </div>
    </div>
  );
}

function WidgetConfigurationSection({ resellerId }: { resellerId: string }) {
  const { data: widgetData } = useQuery({
    queryKey: [`/api/wholesale/white-label/quote-widget/${resellerId}`],
  });

  const [copied, setCopied] = useState(false);

  const handleCopyCode = () => {
    if (widgetData?.widgetCode) {
      navigator.clipboard.writeText(widgetData.widgetCode);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>Embeddable Quote Widget</CardTitle>
          <CardDescription>
            Add this quote widget to any webpage to capture leads directly
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {widgetData && (
            <>
              <div>
                <div className="flex items-center justify-between mb-2">
                  <Label>Widget Code</Label>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleCopyCode}
                    data-testid="button-copy-widget-code"
                  >
                    {copied ? 'Copied!' : 'Copy Code'}
                  </Button>
                </div>
                <Textarea
                  value={widgetData.widgetCode}
                  readOnly
                  rows={10}
                  className="font-mono text-sm"
                  data-testid="textarea-widget-code"
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Widget Preview URL</Label>
                  <Input
                    value={widgetData.previewUrl}
                    readOnly
                    className="text-sm"
                    data-testid="input-preview-url"
                  />
                </div>
                <div>
                  <Label>Documentation</Label>
                  <Input
                    value={widgetData.documentation}
                    readOnly
                    className="text-sm"
                    data-testid="input-documentation-url"
                  />
                </div>
              </div>

              <div className="p-4 bg-muted rounded-lg">
                <h4 className="font-medium mb-2">Integration Instructions:</h4>
                <ol className="text-sm space-y-1 list-decimal list-inside">
                  <li>Copy the widget code above</li>
                  <li>Paste it into your website's HTML where you want the quote form to appear</li>
                  <li>The widget will automatically match your branding configuration</li>
                  <li>Leads will be sent directly to your wholesale dashboard</li>
                </ol>
              </div>
            </>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/WholesaleBulkPricing.ts.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  Upload, 
  Download, 
  Calculator, 
  FileSpreadsheet, 
  CheckCircle,
  AlertCircle,
  Trash2,
  Plus
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface BulkQuoteItem {
  id: string;
  vin: string;
  year?: number;
  make?: string;
  model?: string;
  productId: string;
  term: string;
  zip: string;
  customerEmail?: string;
  basePremium?: number;
  totalPremium?: number;
  commission?: number;
  status: "pending" | "processed" | "error";
  errorMessage?: string;
}

export default function WholesaleBulkPricing() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [bulkItems, setBulkItems] = useState<BulkQuoteItem[]>([]);
  const [processingResults, setProcessingResults] = useState<BulkQuoteItem[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);

  const addBulkItem = () => {
    const newItem: BulkQuoteItem = {
      id: `bulk-${Date.now()}`,
      vin: "",
      productId: "",
      term: "",
      zip: "",
      customerEmail: "",
      status: "pending"
    };
    setBulkItems([...bulkItems, newItem]);
  };

  const updateBulkItem = (id: string, field: string, value: string) => {
    setBulkItems(prev => 
      prev.map(item => 
        item.id === id ? { ...item, [field]: value } : item
      )
    );
  };

  const removeBulkItem = (id: string) => {
    setBulkItems(prev => prev.filter(item => item.id !== id));
  };

  const processBulkQuotes = useMutation({
    mutationFn: async (items: BulkQuoteItem[]) => {
      return await apiRequest("POST", "/api/wholesale/bulk-quotes", { items });
    },
    onSuccess: (data) => {
      setProcessingResults(data.results);
      toast({
        title: "Bulk Processing Complete",
        description: `Processed ${data.results.length} quotes successfully`,
      });
    },
    onError: () => {
      toast({
        title: "Processing Error",
        description: "Failed to process bulk quotes. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleProcessBulk = () => {
    if (bulkItems.length === 0) {
      toast({
        title: "No Items",
        description: "Please add items to process",
        variant: "destructive",
      });
      return;
    }

    const validItems = bulkItems.filter(item => item.vin && item.productId && item.term);
    if (validItems.length === 0) {
      toast({
        title: "Invalid Items",
        description: "Please ensure all items have VIN, Product, and Term specified",
        variant: "destructive",
      });
      return;
    }

    setIsProcessing(true);
    processBulkQuotes.mutate(validItems);
    setTimeout(() => setIsProcessing(false), 2000);
  };

  const exportResults = () => {
    if (processingResults.length === 0) {
      toast({
        title: "No Results",
        description: "No processed results to export",
        variant: "destructive",
      });
      return;
    }

    // Create CSV content
    const headers = ["VIN", "Product", "Term", "Customer Email", "Base Premium", "Total Premium", "Commission", "Status"];
    const csvContent = [
      headers.join(","),
      ...processingResults.map(item => [
        item.vin,
        item.productId,
        item.term,
        item.customerEmail || "",
        item.basePremium || "",
        item.totalPremium || "",
        item.commission || "",
        item.status
      ].join(","))
    ].join("\n");

    // Download CSV
    const blob = new Blob([csvContent], { type: "text/csv" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `bulk-quotes-${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const uploadCSV = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const csv = e.target?.result as string;
      const lines = csv.split('\n');
      const headers = lines[0].split(',');

      const newItems: BulkQuoteItem[] = lines.slice(1)
        .filter(line => line.trim())
        .map((line, index) => {
          const values = line.split(',');
          return {
            id: `csv-${Date.now()}-${index}`,
            vin: values[0] || "",
            productId: values[1] || "",
            term: values[2] || "",
            zip: values[3] || "",
            customerEmail: values[4] || "",
            status: "pending" as const
          };
        });

      setBulkItems([...bulkItems, ...newItems]);
      toast({
        title: "CSV Imported",
        description: `Imported ${newItems.length} items from CSV`,
      });
    };
    reader.readAsText(file);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-4 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <Calculator className="h-8 w-8 text-blue-600 mr-3" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Bulk Pricing Tool</h1>
                <p className="text-gray-600">Process multiple quotes simultaneously</p>
              </div>
            </div>
            <div className="flex space-x-4">
              <Button variant="outline" onClick={() => document.getElementById('csv-upload')?.click()}>
                <Upload className="h-4 w-4 mr-2" />
                Import CSV
              </Button>
              <input
                id="csv-upload"
                type="file"
                accept=".csv"
                onChange={uploadCSV}
                className="hidden"
              />
              <Button variant="outline" onClick={exportResults}>
                <Download className="h-4 w-4 mr-2" />
                Export Results
              </Button>
            </div>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        {/* Bulk Input Section */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <span>Bulk Quote Input</span>
              <Button onClick={addBulkItem} size="sm">
                <Plus className="h-4 w-4 mr-2" />
                Add Item
              </Button>
            </CardTitle>
            <CardDescription>
              Add multiple vehicles for bulk quote processing. You can also import from CSV.
            </CardDescription>
          </CardHeader>
          <CardContent>
            {bulkItems.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                <FileSpreadsheet className="mx-auto h-12 w-12 mb-4" />
                <p>No items added yet. Click "Add Item" or import a CSV to get started.</p>
              </div>
            ) : (
              <div className="space-y-4">
                {bulkItems.map((item, index) => (
                  <Card key={item.id} className="border-l-4 border-l-blue-500">
                    <CardContent className="pt-4">
                      <div className="grid md:grid-cols-6 gap-4 items-end">
                        <div>
                          <Label htmlFor={`vin-${item.id}`}>VIN</Label>
                          <Input
                            id={`vin-${item.id}`}
                            placeholder="17-digit VIN"
                            value={item.vin}
                            onChange={(e) => updateBulkItem(item.id, "vin", e.target.value)}
                            maxLength={17}
                          />
                        </div>

                        <div>
                          <Label htmlFor={`product-${item.id}`}>Product</Label>
                          <Select 
                            value={item.productId}
                            onValueChange={(value) => updateBulkItem(item.id, "productId", value)}
                          >
                            <SelectTrigger>
                              <SelectValue placeholder="Select" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="auto-advantage-wholesale">Auto Advantage</SelectItem>
                              <SelectItem value="home-protection-wholesale">Home Protection</SelectItem>
                              <SelectItem value="all-vehicle-wholesale">All-Vehicle</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>

                        <div>
                          <Label htmlFor={`term-${item.id}`}>Term</Label>
                          <Select 
                            value={item.term}
                            onValueChange={(value) => updateBulkItem(item.id, "term", value)}
                          >
                            <SelectTrigger>
                              <SelectValue placeholder="Select" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="12">12 months</SelectItem>
                              <SelectItem value="24">24 months</SelectItem>
                              <SelectItem value="36">36 months</SelectItem>
                              <SelectItem value="48">48 months</SelectItem>
                              <SelectItem value="60">60 months</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>

                        <div>
                          <Label htmlFor={`zip-${item.id}`}>ZIP</Label>
                          <Input
                            id={`zip-${item.id}`}
                            placeholder="ZIP Code"
                            value={item.zip}
                            onChange={(e) => updateBulkItem(item.id, "zip", e.target.value)}
                          />
                        </div>

                        <div>
                          <Label htmlFor={`email-${item.id}`}>Customer Email</Label>
                          <Input
                            id={`email-${item.id}`}
                            placeholder="Email (optional)"
                            value={item.customerEmail}
                            onChange={(e) => updateBulkItem(item.id, "customerEmail", e.target.value)}
                          />
                        </div>

                        <div>
                          <Button 
                            variant="outline" 
                            size="sm"
                            onClick={() => removeBulkItem(item.id)}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}

                <div className="flex justify-center pt-4">
                  <Button 
                    onClick={handleProcessBulk}
                    disabled={isProcessing || bulkItems.length === 0}
                    size="lg"
                  >
                    {isProcessing ? "Processing..." : `Process ${bulkItems.length} Quotes`}
                  </Button>
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Results Section */}
        {processingResults.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Processing Results</CardTitle>
              <CardDescription>
                Results from your bulk quote processing
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>VIN</TableHead>
                    <TableHead>Product</TableHead>
                    <TableHead>Term</TableHead>
                    <TableHead>Base Premium</TableHead>
                    <TableHead>Total Premium</TableHead>
                    <TableHead>Commission</TableHead>
                    <TableHead>Status</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {processingResults.map((result) => (
                    <TableRow key={result.id}>
                      <TableCell className="font-mono text-sm">{result.vin}</TableCell>
                      <TableCell>{result.productId}</TableCell>
                      <TableCell>{result.term} months</TableCell>
                      <TableCell>${result.basePremium?.toLocaleString()}</TableCell>
                      <TableCell>${result.totalPremium?.toLocaleString()}</TableCell>
                      <TableCell>${result.commission?.toLocaleString()}</TableCell>
                      <TableCell>
                        <div className="flex items-center">
                          {result.status === "processed" ? (
                            <>
                              <CheckCircle className="h-4 w-4 text-green-600 mr-2" />
                              <Badge variant="default">Success</Badge>
                            </>
                          ) : result.status === "error" ? (
                            <>
                              <AlertCircle className="h-4 w-4 text-red-600 mr-2" />
                              <Badge variant="destructive">Error</Badge>
                            </>
                          ) : (
                            <Badge variant="secondary">Pending</Badge>
                          )}
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        )}

        {/* CSV Template Section */}
        <Card className="mt-8">
          <CardHeader>
            <CardTitle>CSV Import Template</CardTitle>
            <CardDescription>
              Use this template format for bulk importing quotes
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="bg-gray-50 p-4 rounded-lg">
              <code className="text-sm">
                VIN,ProductID,Term,ZIP,CustomerEmail<br/>
                1HGCM82633A123456,auto-advantage-wholesale,36,12345,customer1@example.com<br/>
                2HGCM82633A123457,home-protection-wholesale,24,54321,customer2@example.com
              </code>
            </div>
            <p className="text-sm text-gray-600 mt-2">
              Required fields: VIN, ProductID, Term. Optional: ZIP, CustomerEmail
            </p>
          </CardContent>
        </Card>
      </main>
    </div>
  );
}

